import React, { useState } from "react";
import NavbarAdmin from "../../components/NavbarAdmin";
import Seat from "../../components/Seat";
import ScheduleControls from "../../components/ScheduleControls";
import "../styles/BookingManagement.css";
import { SeatProps } from "../../interfaces/seats";
import { mockMovies, mockCinemas } from "../../data/mockData"; // Import mock data

interface AdminSeatData {
  id: string;
  row: string;
  number: number;
  status: "available" | "sold";
  actualStatus: "available" | "reserved" | "sold";
  username: string;
}

const BookingManagement = () => {
  const [selectedMovie, setSelectedMovie] = useState("");
  const [selectedLocation, setSelectedLocation] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [seats, setSeats] = useState<AdminSeatData[][]>(generateSeats());
  const [selectedSeat, setSelectedSeat] = useState<{row: number, col: number} | null>(null);

  function generateSeats(): AdminSeatData[][] {
    const rows = 5;
    const cols = 10;
    return Array.from({ length: rows }, (_, i) => 
      Array.from({ length: cols }, (_, j) => ({
        id: `seat-${i}-${j}`,
        row: `Row ${i+1}`,
        number: j+1,
        status: "available",
        actualStatus: "available",
        username: ""
      }))
    );
  }

  const handleSeatClick = (rowIndex: number, colIndex: number) => {
    const seat = seats[rowIndex][colIndex];
    
    setSelectedSeat(prev => 
      prev?.row === rowIndex && prev?.col === colIndex ? null : {row: rowIndex, col: colIndex}
    );

    if (seat.actualStatus === "reserved") {
      if (window.confirm(`Remove reservation for ${seat.username}?`)) {
        const newSeats = [...seats];
        newSeats[rowIndex][colIndex] = {
          ...seat,
          status: "available",
          actualStatus: "available",
          username: ""
        };
        setSeats(newSeats);
      }
    }
  };

  const toSeatProps = (seat: AdminSeatData): SeatProps => ({
    id: seat.id,
    row: seat.row,
    number: seat.number,
    status: seat.status
  });

  return (
    <div className="booking-management">
      <NavbarAdmin />
      
      {/* Updated ScheduleControls with all required props */}
      <ScheduleControls
        selectedMovie={selectedMovie}
        setSelectedMovie={setSelectedMovie}
        selectedLocation={selectedLocation}
        setSelectedLocation={setSelectedLocation}
        selectedDate={selectedDate}
        setSelectedDate={setSelectedDate}
        selectedTime={selectedTime}
        setSelectedTime={setSelectedTime}
        movies={mockMovies}
        cinemas={mockCinemas}
      />

      <div className="screen-label">Screen</div>
      
      <div className="seat-layout">
        {seats.map((row, rowIndex) => (
          <div key={`row-${rowIndex}`} className="seat-row">
            <div className="row-label">{row[0].row}</div>
            {row.map((seat, colIndex) => (
              <div key={seat.id} className="seat-container">
                <Seat
                  seat={toSeatProps(seat)}
                  isSelected={selectedSeat?.row === rowIndex && selectedSeat?.col === colIndex}
                  onClick={() => handleSeatClick(rowIndex, colIndex)}
                />
                {seat.actualStatus === "reserved" && (
                  <span className="reserved-badge" title={`Reserved by ${seat.username}`}>R</span>
                )}
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="seat-legend">
        <div className="legend-item">
          <span className="legend-color available"></span>
          <span>Available</span>
        </div>
        <div className="legend-item">
          <span className="legend-color reserved"></span>
          <span>Reserved</span>
        </div>
        <div className="legend-item">
          <span className="legend-color sold"></span>
          <span>Sold</span>
        </div>
        <div className="legend-item">
          <span className="legend-color selected"></span>
          <span>Selected</span>
        </div>
      </div>
    </div>
  );
};

export default BookingManagement;
