import React, { useState } from "react";
import "./styles/AddMovie.css";

const AddMovie: React.FC = () => {
  const [movieName, setMovieName] = useState("");
  const [releaseDate, setReleaseDate] = useState("");
  const [duration, setDuration] = useState("30");
  const [movieType, setMovieType] = useState("Action");
  const [schedule, setSchedule] = useState<string[]>([]);
  const [locations, setLocations] = useState<string[]>([]);
  const [poster, setPoster] = useState<File | null>(null);

  const movieTypes = ["Action", "Drama", "Comedy", "Horror", "Sci-Fi"];
  const locationOptions = ["Kuala Lumpur", "Penang", "Johor", "Sabah", "Sarawak"];
  const durationOptions = ["30", "60", "90", "120", "150"];

  const handleScheduleChange = (day: string) => {
    setSchedule((prev) =>
      prev.includes(day) ? prev.filter((d) => d !== day) : [...prev, day]
    );
  };

  const handleLocationChange = (location: string) => {
    setLocations((prev) =>
      prev.includes(location) ? prev.filter((l) => l !== location) : [...prev, location]
    );
  };

  const handlePosterUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setPoster(e.target.files[0]);
    }
  };

  return (
    <div className="add-movie-container">
      <h2>Manage Movie</h2>
      <div className="movie-form">
        <div className="poster-upload">
          <input type="file" accept="image/*" onChange={handlePosterUpload} />
          {poster && <img src={URL.createObjectURL(poster)} alt="Movie Poster" />}
        </div>
        <label>Movie Name:</label>
        <input type="text" value={movieName} onChange={(e) => setMovieName(e.target.value)} />

        <label>Release Date:</label>
        <input type="date" value={releaseDate} onChange={(e) => setReleaseDate(e.target.value)} />

        <label>Duration (mins):</label>
        <select value={duration} onChange={(e) => setDuration(e.target.value)}>
          {durationOptions.map((time) => (
            <option key={time} value={time}>{time} mins</option>
          ))}
        </select>

        <label>Movie Type:</label>
        <select value={movieType} onChange={(e) => setMovieType(e.target.value)}>
          {movieTypes.map((type) => (
            <option key={type} value={type}>{type}</option>
          ))}
        </select>

        <label>Schedules:</label>
        <div className="schedule-options">
          {["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"].map((day) => (
            <label key={day}>
              <input type="checkbox" checked={schedule.includes(day)} onChange={() => handleScheduleChange(day)} />
              {day}
            </label>
          ))}
        </div>

        <label>Locations:</label>
        <div className="location-options">
          {locationOptions.map((loc) => (
            <label key={loc}>
              <input type="checkbox" checked={locations.includes(loc)} onChange={() => handleLocationChange(loc)} />
              {loc}
            </label>
          ))}
        </div>
      </div>

      <div className="buttons">
        <button>Add Movie</button>
        <button>Edit Movie</button>
        <button>Delete Movie</button>
      </div>
    </div>
  );
};

export default AddMovie;
