import React, { useState } from "react";
import { Card, CardContent } from "../components/ui/card";
import Button from "../components/ui/button";
import { HeartIcon } from "lucide-react";
import "../styles/dashboard.css";

const movies = [
  {
    title: "NE ZHA 2 哪吒之魔童闹海",
    genre: "Animation, Action, Drama",
    duration: "2 hr 24 min",
    language: "Mandarin",
    showtimes: {
      IMAX: ["1:00 PM", "3:45 PM", "6:30 PM", "9:30 PM", "12:30 AM 28 Mar 2025"],
      INDULGE: ["1:30 PM", "2:15 PM", "4:45 PM", "5:30 PM", "8:00 PM"],
      BEANIE: ["11:15 PM", "11:30 PM"],
    },
  },
];

export default function AdminDashboard() {
  const [selectedDate, setSelectedDate] = useState("27 MAR");

  return (
    <div className="dashboard-container">
      {/* Date Selection */}
      <div className="date-selector">
        {["27 MAR", "28 MAR", "29 MAR", "30 MAR", "31 MAR", "1 APR", "2 APR", "3 APR"].map((date) => (
          <Button
            key={date}
            className={`date-btn ${selectedDate === date ? "active" : ""}`}
            onClick={() => setSelectedDate(date)}
          >
            {date}
          </Button>
        ))}
      </div>

      {/* Movie List */}
      <div className="movie-list">
        {movies.map((movie, index) => (
          <Card key={index} className="movie-card">
            <CardContent>
              <div className="movie-header">
                <div>
                  <h2 className="movie-title">{movie.title}</h2>
                  <p className="movie-details">{movie.genre} | {movie.duration} | {movie.language}</p>
                </div>
                <Button variant="outline" size="icon">
                  <HeartIcon className="heart-icon" />
                </Button>
              </div>

              {/* Showtimes */}
              <div className="showtimes">
                {Object.entries(movie.showtimes).map(([category, times]) => (
                  <div key={category} className="showtime-category">
                    <h3 className={`showtime-label ${category.toLowerCase()}`}>{category}</h3>
                    <div className="time-buttons">
                      {times.map((time, i) => (
                        <Button key={i} className="time-btn">
                          {time}
                        </Button>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
