"use client";
import { useEffect, useMemo, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { ChevronDown, ChevronLeft } from "lucide-react";
import { mockMovies } from "../data/mockData";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import "../styles/moviedetails.css";

// Utility to generate rolling date range
const generateDateRange = (days: number): string[] => {
  const dates: string[] = [];
  const today = new Date();
  for (let i = 0; i < days; i++) {
    const future = new Date(today);
    future.setDate(today.getDate() + i);
    dates.push(future.toISOString().split("T")[0]);
  }
  return dates;
};

const MovieDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const movie = mockMovies.find((m) => m.id === Number(id));

  const [selectedDate, setSelectedDate] = useState<string>("");

  // Get all session dates from screenings and sort them
  const uniqueDates = useMemo(() => {
    if (!movie) return [];
    const allDates = movie.screenings.flatMap((screening) =>
      screening.sessions.map((session) => session.date)
    );
    const unique = Array.from(new Set(allDates));
    unique.sort((a, b) => new Date(a).getTime() - new Date(b).getTime());
    return unique;
  }, [movie]);

  // Initialize selectedDate
  useEffect(() => {
    if (!movie) return;
    const today = new Date().toISOString().split("T")[0];
    const availableDates = movie.screenings.flatMap((s) =>
      s.sessions.map((s) => s.date)
    );
    setSelectedDate(availableDates.includes(today) ? today : availableDates[0]);
  }, [movie]);

  const filteredScreenings = useMemo(() => {
    return movie
      ? movie.screenings
          .map((screening) => ({
            cinema: screening.cinema,
            sessions: screening.sessions.filter(
              (session) => session.date === selectedDate
            ),
          }))
          .filter((s) => s.sessions.length > 0)
      : [];
  }, [movie, selectedDate]);

  const dateRange = generateDateRange(12); // 12 days ahead
  const availableSessionDates = new Set(
    movie?.screenings.flatMap((s) => s.sessions.map((s) => s.date)) || []
  );

  if (!movie) {
    return (
      <>
        <Navbar />
        <div className="not-found">Movie not found.</div>
        <Footer />
      </>
    );
  }

  return (
    <>
      <Navbar />
      <div className="movie-details-container">
        {/* Banner */}
        <div
          className="banner"
          style={{ backgroundImage: `url(${movie.banner})` }}
        >
          <button className="back-button" onClick={() => navigate(-1)}>
            <ChevronLeft size={20} />
            Back
          </button>
          <div className="details-overlay">
            <h1 className="title">{movie.title}</h1>
            <p className="metadata">
              {movie.genre} | {movie.duration} | {movie.languages.join(", ")}
            </p>
            <div className="button-group">
              <button className="info-btn">
                More Info <ChevronDown size={16} />
              </button>
              <button className="trailer-btn">Watch Trailer</button>
            </div>
          </div>
        </div>

        {/* Booking Section */}
        <div className="booking-section">
          {/* Scrollable Date Bar */}
          <div className="dates-scrollbar">
            {dateRange.map((date) => {
              const isActive = selectedDate === date;
              const isAvailable = availableSessionDates.has(date);
              return (
                <div
                  key={date}
                  className={`date-tab ${isActive ? "active" : ""} ${
                    isAvailable ? "clickable" : "disabled"
                  }`}
                  onClick={() => isAvailable && setSelectedDate(date)}
                >
                  <span className="day">
                    {new Date(date).toLocaleDateString("en", {
                      weekday: "short",
                    })}
                  </span>
                  <span className="date-text">
                    {new Date(date).toLocaleDateString("en", {
                      day: "2-digit",
                      month: "short",
                    })}
                  </span>
                </div>
              );
            })}
          </div>

          {/* Cinema & Times */}
          <div className="cinema-info-header centered">
            <p className="location">Cinemas Showing on {selectedDate}</p>
            <div className="legend">
              <span className="available">âœ“ Available</span>
              <span className="fast">ðŸŸ¡ Selling fast</span>
              <span className="sold">ðŸ”´ Sold out</span>
            </div>
          </div>

          <div className="cinema-list centered">
            {filteredScreenings.map((screen) => (
              <div key={screen.cinema} className="cinema-block">
                <h4>{screen.cinema}</h4>
                <div className="times">
                  {screen.sessions.map((s) => (
                    <button key={`${s.date}-${s.time}`} className="time-btn">
                      {s.time}
                    </button>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <button className="cinema-details-toggle centered">
            <span>ðŸŽ¬ Cinema Details</span>
            <ChevronDown size={16} />
          </button>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default MovieDetails;
