import apiRoutes from "../config/apiRoutes"

export const register = async (username: string, password: string, email: string) => {
    try {
        const response = await fetch(apiRoutes.auth.register, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ username, password, email }),
        })

        const data = await response.json()

        if (!response.ok) {
            return {
                success: false,
                message: data.message || "Registration failed",
            }
        }

        return {
            success: true,
            message: data.message,
            data: {
                userId: data.userId, // <- 就是从后端来的 string userId
            },
        }
    } catch (error: any) {
        return {
            success: false,
            message: error.message || "Something went wrong",
        }
    }
}


export const verifyEmail = async (userId: string, code: string) => {
    try {
        const response = await fetch(apiRoutes.auth.verifyEmail, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({ userId, code }),
        })

        const data = await response.json()

        if (!response.ok) {
            return {
                success: false,
                message: data.message || "Verification failed",
            }
        }

        return { success: true, message: data.message }
    } catch (error: any) {
        return {
            success: false,
            message: error.message || "An unknown error occurred",
        }
    }
}

export const refreshToken = async (refreshToken: string) => {
    try {
        const response = await fetch(apiRoutes.auth.refresh, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                RefreshToken: refreshToken,
            }),
        })

        const data = await response.json()

        if (!response.ok) {
            return {
                success: false,
                message: data.message || "Token refresh failed",
            }
        }

        return { success: true, data }
    } catch (error: any) {
        return {
            success: false,
            message: error.message || "An unknown error occurred",
        }
    }
}

interface LoginResponse {
    success: boolean
    message?: string
    data?: {
        token: string
        refreshToken: string
        user: {
            userId: string
            name: string
            email: string
        }
    }
}

export const login = async (email: string, password: string): Promise<LoginResponse> => {
    try {
        const response = await fetch(apiRoutes.auth.login, {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                Email: email,
                Password: password,
            }),
        })

        const data = await response.json()

        if (!response.ok) {
            return {
                success: false,
                message: data.message || "Login failed. Please check your credentials.",
            }
        }

        return {
            success: true,
            data: {
                token: data.token,
                refreshToken: data.refreshToken,
                user: data.user,
            },
        }
    } catch (error: any) {
        console.error("Login error:", error)
        return {
            success: false,
            message: error.message || "An unknown error occurred",
        }
    }
}

export const logout = () => {
    localStorage.removeItem("authToken")
    localStorage.removeItem("refreshToken")
    localStorage.removeItem("user")
}

export const isAuthenticated = (): boolean => {
    return localStorage.getItem("authToken") !== null
}

export const getCurrentUser = () => {
    const userStr = localStorage.getItem("user")
    if (!userStr) return null

    try {
        return JSON.parse(userStr)
    } catch (e) {
        return null
    }
}
