import React, { useState } from "react";
import "../admins/styles/filterListbox.css";

interface FilterListboxProps {
  onFilterChange: (filter: string) => void;
}

const FilterListbox: React.FC<FilterListboxProps> = ({ onFilterChange }) => {
  const [filter, setFilter] = useState("today");

  const filters = ["today", "upcoming", "out-dated"];

  const handleFilterChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const selectedFilter = e.target.value;
    setFilter(selectedFilter);
    onFilterChange(selectedFilter);  // Notify parent component of filter change
  };

  return (
    <div className="mb-4">
      <h2 className="text-xl font-semibold mb-2">ðŸŽ¬ Filter Movies</h2>
      <div className="flex gap-4">
        <label htmlFor="filter" className="mr-2">Select Filter:</label>
        <select
          id="filter"
          className="px-4 py-2 rounded border"
          value={filter}
          onChange={handleFilterChange}
        >
          {filters.map((item) => (
            <option key={item} value={item}>
              {item.charAt(0).toUpperCase() + item.slice(1)}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default FilterListbox;
