// src/admins/pages/BookingManagement.tsx
import React, { useState } from "react";
import NavbarAdmin from "../../components/NavbarAdmin";
import Seat from "../../components/Seat";
import ScheduleControls from "../../components/ScheduleControls";
import "../styles/BookingManagement.css";

interface SeatData {
  id: string;
  row: string;
  number: number;
  status: "available" | "reserved" | "sold";
  username: string;
}

const BookingManagement = () => {
  // ... existing state declarations ...

  function generateSeats(): SeatData[][] {
    let seatLayout: SeatData[][] = [];
    for (let i = 0; i < 5; i++) {
      let row: SeatData[] = [];
      for (let j = 0; j < 10; j++) {
        row.push({
          id: `seat-${i}-${j}`,
          row: `Row ${i+1}`,
          number: j+1,
          status: "available",
          username: ""
        });
      }
      seatLayout.push(row);
    }
    return seatLayout;
  }

  const handleSeatClick = (rowIndex: number, colIndex: number) => {
    setSelectedSeat(prev => 
      prev?.row === rowIndex && prev?.col === colIndex ? null : {row: rowIndex, col: colIndex}
    );

    let newSeats = [...seats];
    let seat = newSeats[rowIndex][colIndex];
    
    if (seat.status === "reserved") {
      if (window.confirm("Remove this reservation?")) {
        seat.status = "available";
        seat.username = "";
        setSeats(newSeats);
      }
    }
  };

  return (
    <div className="booking-management">
      <NavbarAdmin />
      
      <ScheduleControls
        selectedMovie={selectedMovie}
        setSelectedMovie={setSelectedMovie}
        selectedLocation={selectedLocation}
        setSelectedLocation={setSelectedLocation}
        selectedDate={selectedDate}
        setSelectedDate={setSelectedDate}
        selectedTime={selectedTime}
        setSelectedTime={setSelectedTime}
      />
      
      <div className="seat-layout">
        {seats.map((row, rowIndex) => (
          <div key={`row-${rowIndex}`} className="seat-row">
            {row.map((seat, colIndex) => {
              // Convert "reserved" status to "sold" for the Seat component
              const seatForDisplay = {
                ...seat,
                status: seat.status === "reserved" ? "sold" : seat.status
              };

              return (
                <Seat
                  key={seat.id}
                  seat={seatForDisplay}
                  isSelected={selectedSeat?.row === rowIndex && selectedSeat?.col === colIndex}
                  onClick={() => handleSeatClick(rowIndex, colIndex)}
                />
              );
            })}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookingManagement;
