import React from "react";
import { SeatProps as SeatData } from "../interfaces/seats"; // alias to avoid name clash

interface SeatComponentProps {
  seat: SeatData;
  isSelected: boolean;
  onClick: () => void;
}

const Seat = ({ seat, isSelected, onClick }: SeatComponentProps) => {
  const getIcon = () => {
    if (seat.status === "sold") {
      return (
        <svg width="24" height="24" fill="#dc2626" viewBox="0 0 24 24">
          <circle cx="12" cy="12" r="10" />
          <line x1="6" y1="6" x2="18" y2="18" stroke="white" strokeWidth="2" />
        </svg>
      );
    } else if (isSelected) {
      return (
        <svg width="24" height="24" fill="#3b82f6" viewBox="0 0 24 24">
          <rect x="4" y="4" width="16" height="16" rx="4" />
        </svg>
      );
    } else {
      return (
        <svg width="24" height="24" fill="#22c55e" viewBox="0 0 24 24">
          <circle cx="12" cy="12" r="10" />
        </svg>
      );
    }
  };

  return (
    <div
      className="seat"
      onClick={onClick}
      style={{ cursor: seat.status === "sold" ? "not-allowed" : "pointer" }}
    >
      {getIcon()}
    </div>
  );
};

export default Seat;
