import React, { useState } from 'react';
import NavbarAdmin from '../../components/NavbarAdmin';
import MovieSelector from '../../components/MovieSelector';
import CinemaLocations from '../../components/Location';
import CinemaRoom from '../../components/CinemaRoom';
import TimeSlot from '../../components/TimeSlot';
import { mockMovies, MovieProps } from '../../data/mockData';
import '../styles/Schedule.css';

const Schedule = () => {
  const [selectedMovie, setSelectedMovie] = useState<MovieProps | null>(null);
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedLocations, setSelectedLocations] = useState<string[]>([]);
  const [selectedRoom, setSelectedRoom] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');

  return (
    <div className="schedule-page">
  <NavbarAdmin /> {/* Your navbar component */}
  <div className="schedule-container">
    <div className="schedule-form">
      <div>
        <div className="movie-selector">
          <label>Select Movie :</label>
          <select value={selectedMovie} onChange={(e) => setSelectedMovie(e.target.value)}>
            <option value="">Movie Listbox</option>
            {/* Your movie options */}
          </select>
        </div>
        
        <div className="movie-preview">
          {selectedMovieImage ? (
            <img src={selectedMovieImage || "/placeholder.svg"} alt={selectedMovie} />
          ) : (
            <div>
              display first movie image, if select other movie will change to the selected movie image
            </div>
          )}
        </div>
      </div>
      
      <div>
        <div className="form-section date-selector">
          <label>Select date :</label>
          <input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
        </div>
        
        <div className="form-section">
          <label>Select a location :</label>
          <div className="location-options">
            {locations.map(location => (
              <button 
                key={location}
                className={`location-option ${selectedLocation === location ? 'selected' : ''}`}
                onClick={() => setSelectedLocation(location)}
              >
                {location}
              </button>
            ))}
          </div>
        </div>
        
        <div className="form-section">
          <label>Cinema Room :</label>
          <div className="room-options">
            {rooms.map(room => (
              <button 
                key={room}
                className={`room-option ${selectedRoom === room ? 'selected' : ''}`}
                onClick={() => setSelectedRoom(room)}
              >
                {room}
              </button>
            ))}
          </div>
        </div>
        
        <div className="form-section">
          <label>Time Slot :</label>
          <div className="time-options">
            {timeSlots.map(time => (
              <button 
                key={time}
                className={`time-option ${selectedTimeSlot === time ? 'selected' : ''}`}
                onClick={() => setSelectedTimeSlot(time)}
              >
                {time}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
    
    <div className="buttons">
      <button className="schedule-button">Schedule Movie</button>
    </div>
  </div>
</div>
  );
};

export default Schedule;
