import React from "react";
import { useParams } from "react-router-dom";
import "../styles/moviedetails.css";
import { MovieProps } from "../interfaces/movies";

// Temporary mock data (replace with API call later)
const mockMovies: MovieProps[] = [
  {
    id: 1,
    title: "Bad Boys",
    image: "https://imgur.com/estKH6K.png",
    banner: "https://imgur.com/exampleBanner1.png",
    rating: "P12",
    category: "NOW SHOWING",
    releaseDate: "2025-04-08",
    genre: "Action, Comedy",
    languages: ["English"],
    duration: "1h 50m",
    hasBookNow: true,
    dates: ["2025-04-08", "2025-04-09", "2025-04-10"],
    screenings: [
      {
        cinema: "Boulevard Bintulu",
        times: ["1:00 PM", "3:30 PM", "8:15 PM"],
      },
      {
        cinema: "Pavilion KL",
        times: ["12:45 PM", "5:00 PM", "9:30 PM"],
      },
    ],
  },
  {
    id: 2,
    title: "Inception",
    image: "https://imgur.com/LRvKBpv.png",
    banner: "https://imgur.com/exampleBanner2.png",
    rating: "P13",
    category: "NOW SHOWING",
    releaseDate: "2025-04-08",
    genre: "Sci-Fi, Thriller",
    languages: ["English", "Japanese"],
    duration: "2h 28m",
    hasBookNow: true,
    dates: ["2025-04-08", "2025-04-09"],
    screenings: [
      {
        cinema: "1 Utama",
        times: ["11:00 AM", "2:00 PM", "6:45 PM"],
      },
    ],
  },
];

const MovieDetail: React.FC = () => {
  const { id } = useParams();
  const movie = mockMovies.find((m) => m.id === parseInt(id || "", 10));

  if (!movie) {
    return (
      <div style={{ color: "white", padding: "2rem" }}>Movie not found.</div>
    );
  }

  return (
    <div className="movie-detail">
      {/* Banner */}
      <div
        className="movie-banner"
        style={{
          backgroundImage: `url(${movie.banner})`,
        }}
      >
        <div className="overlay">
          <h1 className="movie-title">{movie.title}</h1>
          <div className="movie-info">
            <span>{movie.genre}</span> | <span>{movie.duration}</span> |{" "}
            <span>{movie.languages?.join(", ")}</span>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="movie-content">
        <h3>Available Dates</h3>
        <div className="dates">
          {movie.dates?.map((date) => (
            <div key={date} className="date-tag">
              {new Date(date).toDateString()}
            </div>
          ))}
        </div>

        <h3>Choose Cinema</h3>
        {movie.screenings?.map((screen) => (
          <div key={screen.cinema} className="cinema-block">
            <h4>{screen.cinema}</h4>
            <div className="screening-times">
              {screen.times.map((time) => (
                <button key={time} className="time-button">
                  {time}
                </button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MovieDetail;
