import React, { useState } from "react";
import NavbarAdmin from "../../components/NavbarAdmin";
import Seat from "../../components/Seat";
import "../styles/BookingManagement.css";

// This should match the SeatData interface from your friend's component
interface SeatData {
  status: "available" | "reserved" | "sold"; // Note: using "sold" instead of "purchased" to match Seat component
  username: string;
}

const BookingManagement = () => {
  const [selectedMovie, setSelectedMovie] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [seats, setSeats] = useState<SeatData[][]>(generateSeats());
  const [selectedSeat, setSelectedSeat] = useState<{row: number, col: number} | null>(null);

  function generateSeats(): SeatData[][] {
    let seatLayout: SeatData[][] = [];
    for (let i = 0; i < 5; i++) {
      let row: SeatData[] = [];
      for (let j = 0; j < 10; j++) {
        row.push({ status: "available", username: "" });
      }
      seatLayout.push(row);
    }
    return seatLayout;
  }

  const handleSeatClick = (rowIndex: number, colIndex: number) => {
    setSelectedSeat(prev => 
      prev?.row === rowIndex && prev?.col === colIndex ? null : {row: rowIndex, col: colIndex}
    );

    let newSeats = [...seats];
    let seat = newSeats[rowIndex][colIndex];
    
    if (seat.status === "reserved") {
      if (window.confirm("Remove this reservation?")) {
        seat.status = "available";
        seat.username = "";
        setSeats(newSeats);
      }
    }
    // Add other seat status handling if needed
  };

  return (
    <div className="booking-management">
      <NavbarAdmin />
      <div className="controls">
        <select 
          value={selectedMovie} 
          onChange={(e) => setSelectedMovie(e.target.value)}
        >
          <option value="">Select Movie</option>
          <option value="movie1">Movie 1</option>
          <option value="movie2">Movie 2</option>
        </select>
        
        <input 
          type="date" 
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)} 
        />
        
        <select 
          value={selectedTime}
          onChange={(e) => setSelectedTime(e.target.value)}
        >
          <option value="">Select Time</option>
          <option value="12:00">12:00 PM</option>
          <option value="15:00">3:00 PM</option>
        </select>
      </div>
      
      <div className="seat-layout">
        {seats.map((row, rowIndex) => (
          <div key={`row-${rowIndex}`} className="seat-row">
            {row.map((seat, colIndex) => (
              <Seat
                key={`seat-${rowIndex}-${colIndex}`}
                seat={seat}
                isSelected={selectedSeat?.row === rowIndex && selectedSeat?.col === colIndex}
                onClick={() => handleSeatClick(rowIndex, colIndex)}
              />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookingManagement;
