"use client";
import { useParams, useNavigate } from "react-router-dom";
import { mockMovies } from "../data/mockData";
import { ChevronDown, ChevronLeft } from "lucide-react";
import "../styles/moviedetails.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";

const MovieDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const movie = mockMovies.find((m) => m.id === Number(id));

  if (!movie) {
    return (
      <>
        <Navbar />
        <div className="not-found">Movie not found.</div>
        <Footer />
      </>
    );
  }

  // Collect all unique dates from all screenings
  const allDates = Array.from(
    new Set(
      movie.screenings.flatMap((screen) => screen.sessions.map((s) => s.date))
    )
  ).sort();

  return (
    <>
      <Navbar />
      <div className="movie-details-container">
        {/* Banner Section */}
        <div
          className="banner"
          style={{ backgroundImage: `url(${movie.banner})` }}
        >
          <button className="back-button" onClick={() => navigate(-1)}>
            <ChevronLeft size={20} />
            Back
          </button>
          <div className="details-overlay">
            <h1 className="title">{movie.title}</h1>
            <p className="metadata">
              {movie.genre} | {movie.duration} | {movie.languages.join(", ")}
            </p>
            <div className="button-group">
              <button className="info-btn">
                More Info <ChevronDown size={16} />
              </button>
              <button className="trailer-btn">Watch Trailer</button>
            </div>
          </div>
        </div>

        {/* Booking Section */}
        <div className="booking-section">
          {/* Date Selector */}
          <div className="dates">
            {allDates.map((d, i) => (
              <div key={d} className={`date ${i === 0 ? "active" : ""}`}>
                <span className="day">
                  {new Date(d).toLocaleDateString("en", { weekday: "short" })}
                </span>
                <span className="date-text">
                  {new Date(d).toLocaleDateString("en", {
                    day: "2-digit",
                    month: "short",
                  })}
                </span>
              </div>
            ))}
          </div>

          {/* Cinema & Screening Times */}
          <div className="cinema-info-header">
            <p className="location">Cinemas Showing This Movie</p>
            <div className="legend">
              <span className="available">✓ Available</span>
              <span className="fast">🟡 Selling fast</span>
              <span className="sold">🔴 Sold out</span>
            </div>
          </div>

          <div className="cinema-list">
            {movie.screenings.map((screen) => (
              <div key={screen.cinema} className="cinema-block">
                <h4>{screen.cinema}</h4>
                <div className="times">
                  {screen.sessions.map((session) => (
                    <button
                      key={`${session.date}-${session.time}`}
                      className="time-btn"
                    >
                      {session.time} (
                      {new Date(session.date).toLocaleDateString("en", {
                        day: "2-digit",
                        month: "short",
                      })}
                      )
                    </button>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <button className="cinema-details-toggle">
            <span>🎬 Cinema Details</span>
            <ChevronDown size={16} />
          </button>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default MovieDetails;
