import React, { useState } from "react";
import { Card, CardContent } from "../components/ui/card";
import Button from "../components/ui/button";
import { CalendarIcon, HeartIcon } from "lucide-react";
import "../styles/dashboard.css";

const movies = [
  {
    title: "NE ZHA 2 哪吒之魔童闹海",
    genre: "Animation, Action, Drama",
    duration: "2 hr 24 min",
    language: "Mandarin",
    showtimes: {
      IMAX: ["1:00 PM", "3:45 PM", "6:30 PM", "9:30 PM", "12:30 AM"],
      INDULGE: ["1:30 PM", "2:15 PM", "4:45 PM", "5:30 PM", "8:00 PM"],
      BEANIE: ["11:15 PM", "11:30 PM"],
    },
  },
];

export default function AdminDashboard() {
  const [selectedDate, setSelectedDate] = useState("27 MAR");

  return (
    <div className="p-6 bg-black text-white min-h-screen">
      {/* Date Selection */}
      <div className="flex gap-4 overflow-x-auto pb-2">
        {["27 MAR", "28 MAR", "29 MAR", "30 MAR", "31 MAR"].map((date) => (
          <Button
            key={date}
            variant={selectedDate === date ? "default" : "outline"}
            onClick={() => setSelectedDate(date)}
          >
            {date}
          </Button>
        ))}
      </div>

      {/* Movie List */}
      <div className="mt-6">
        {movies.map((movie, index) => (
          <Card key={index} className="mb-6 bg-gray-900 p-4 rounded-xl">
            <CardContent>
              <div className="flex justify-between">
                <div>
                  <h2 className="text-2xl font-bold">{movie.title}</h2>
                  <p className="text-gray-400">{movie.genre} | {movie.duration} | {movie.language}</p>
                </div>
                <Button variant="outline" size="icon">
                  <HeartIcon className="text-red-500" />
                </Button>
              </div>

              {/* Showtimes */}
              <div className="mt-4">
                {Object.entries(movie.showtimes).map(([category, times]) => (
                  <div key={category} className="mt-2">
                    <h3 className="font-bold text-lg text-blue-400">{category}</h3>
                    <div className="flex gap-2 mt-2">
                      {times.map((time, i) => (
                        <Button key={i} variant="secondary">
                          {time}
                        </Button>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
