import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Import Axios
import { ChevronRight } from "lucide-react";
import "../../styles/landing_page.css"; // Your styles

import { MovieProps } from "../../interfaces/movies";
import { HeroSlideProps } from "../../interfaces/heroSlide";
import { CinemaProps } from "../../interfaces/cinemas";

const CinemaWebsite: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<string>("NOW SHOWING");
  const [currentSlide, setCurrentSlide] = useState<number>(0);
  const [showCinemaDropdown, setShowCinemaDropdown] = useState<boolean>(false);
  const [movies, setMovies] = useState<MovieProps[]>([]);
  const [cinemas, setCinemas] = useState<CinemaProps[]>([]);
  const [heroSlides, setHeroSlides] = useState<HeroSlideProps[]>([]);

  const tabs: string[] = [
    "NOW SHOWING",
    "BOOK EARLY",
    "COMING SOON",
    "GEMBIRAYA",
  ];

  // Auto-advance slideshow
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [heroSlides.length]);

  // Fetch movie, cinema, and hero slide data from the API
  useEffect(() => {
    axios
      .get("/api/movies")
      .then((response) => {
        setMovies(response.data);
      })
      .catch((error) => {
        console.error("Error fetching movies:", error);
      });

    axios
      .get("/api/cinemas")
      .then((response) => {
        setCinemas(response.data);
      })
      .catch((error) => {
        console.error("Error fetching cinemas:", error);
      });

    axios
      .get("/api/hero-slides")
      .then((response) => {
        setHeroSlides(response.data);
      })
      .catch((error) => {
        console.error("Error fetching hero slides:", error);
      });
  }, []);

  const navigateToCinema = (cinemaId: string, movieId?: number) => {
    const path = `/showtimes/${cinemaId}${movieId ? `?movie=${movieId}` : ""}`;
    navigate(path);
  };

  const handleButtonClick = (path: string) => {
    navigate(path);
  };

  return (
    <div className="cinema-container">
      {/* Header */}
      <header className="header">
        <div className="header-left">
          <a href="/" className="logo">
            YourCinema
          </a>
          <div className="dropdown">
            <button
              className="location-button"
              onClick={() => setShowCinemaDropdown(!showCinemaDropdown)}
            >
              <span className="location-icon">üìç</span>
              ALL STATES/AREAS
            </button>
            {showCinemaDropdown && (
              <div className="dropdown-content">
                <h3>Select a Cinema</h3>
                <div className="cinema-dropdown-list">
                  {cinemas.map((cinema) => (
                    <div
                      key={cinema.id}
                      className="cinema-dropdown-item"
                      onClick={() => navigateToCinema(cinema.id)}
                    >
                      {cinema.name}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        <nav className="main-nav">
          <a href="/movies" className="nav-link">
            MOVIES
          </a>
          <a href="/cinemas" className="nav-link">
            CINEMAS
          </a>
          <a href="/food-drinks" className="nav-link">
            FOOD & DRINKS
          </a>
          <a href="/booking" className="nav-link">
            TEST BOOKING PAGE
          </a>
          <div className="dropdown">
            <button className="nav-link dropdown-toggle">
              MORE
              <span className="dropdown-arrow">‚ñº</span>
            </button>
          </div>
        </nav>

        <a href="/login" className="sign-in-button">
          <span className="user-icon">üë§</span>
          SIGN IN
        </a>
      </header>

      {/* Hero Slideshow */}
      <section className="hero-slideshow">
        {heroSlides.map((slide, index) => (
          <div
            key={slide.id}
            className={`hero-slide ${index === currentSlide ? "active" : ""}`}
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${slide.image})`,
            }}
          >
            <div className="hero-content">
              <h1>{slide.title}</h1>
              <p>{slide.subtitle}</p>
              <button
                className="hero-button"
                onClick={() => handleButtonClick(slide.buttonLink)}
              >
                {slide.buttonText}
              </button>
            </div>
          </div>
        ))}
      </section>

      {/* Movie Tabs */}
      <div className="movie-tabs-container">
        <div className="movie-tabs-wrapper">
          <div className="movie-tabs">
            {tabs.map((tab) => (
              <button
                key={tab}
                className={`tab-button ${activeTab === tab ? "active" : ""}`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </button>
            ))}
          </div>
          <a href="/all-movies" className="view-all-link">
            VIEW ALL
            <span className="arrow-icon">‚Üí</span>
          </a>
        </div>

        {/* Movie Grid */}
        <div className="movie-grid">
          {movies.map((movie) => (
            <div key={movie.id} className="movie-card">
              {/* Movie Poster */}
              <div className="movie-poster">
                <img
                  src={movie.image || "/placeholder.svg"}
                  alt={movie.title}
                  className="poster-image"
                />

                {/* Tags */}
                {movie.tag && (
                  <div className={`movie-tag ${movie.tagColor}`}>
                    {movie.tag}
                  </div>
                )}

                {/* Release Date */}
                {movie.releaseDate && (
                  <div className="release-date">{movie.releaseDate}</div>
                )}

                {/* Date for NE ZHA 2 */}
                {movie.date && <div className="movie-date">{movie.date}</div>}

                {/* Rating */}
                <div
                  className={`movie-rating ${
                    movie.rating === "15" ? "rating-15" : "rating-p12"
                  }`}
                >
                  {movie.rating}
                </div>

                {/* Book Now Button */}
                {movie.hasBookNow && (
                  <div className="book-now-container">
                    <button
                      className="book-now-button"
                      onClick={() => navigateToCinema("1utama", movie.id)}
                    >
                      BOOK NOW
                    </button>
                  </div>
                )}
              </div>

              {/* Movie Title */}
              <div
                className="movie-title"
                onClick={() => navigateToCinema("1utama", movie.id)}
              >
                <h3>{movie.title}</h3>
              </div>
            </div>
          ))}

          {/* Carousel Navigation */}
          <button className="carousel-nav-button">
            <span className="carousel-arrow">‚Üí</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default CinemaWebsite;
