"use client";
import { useParams, useNavigate } from "react-router-dom";
import { mockMovies } from "../data/mockData";
import { ChevronDown, ChevronLeft, Info } from "lucide-react";
import "./moviedetails.css";

const MovieDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const movie = mockMovies.find((m) => m.id === Number(id));

  if (!movie) {
    return <div className="not-found">Movie not found.</div>;
  }

  return (
    <div className="movie-details-container">
      <div
        className="banner"
        style={{ backgroundImage: `url(${movie.banner})` }}
      >
        <button className="back-button" onClick={() => navigate(-1)}>
          <ChevronLeft size={20} />
          Back
        </button>
        <div className="details-overlay">
          <h1 className="title">{movie.title}</h1>
          <p className="metadata">
            {movie.genre} | {movie.duration} | {movie.languages.join(", ")}
          </p>
          <div className="button-group">
            <button className="info-btn">
              More Info <ChevronDown size={16} />
            </button>
            <button className="trailer-btn">Watch Trailer</button>
          </div>
        </div>
      </div>

      <div className="booking-section">
        <div className="dates">
          {movie.dates.map((d, i) => (
            <div key={d} className={`date ${i === 0 ? "active" : ""}`}>
              <span className="day">
                {new Date(d).toLocaleDateString("en", {
                  weekday: "short",
                })}
              </span>
              <span className="date-text">
                {new Date(d).toLocaleDateString("en", {
                  day: "2-digit",
                  month: "short",
                })}
              </span>
            </div>
          ))}
        </div>

        <div className="cinema-info-header">
          <p className="location">SARAWAK - {movie.screenings[0]?.cinema}</p>
          <div className="legend">
            <span className="available">âœ“ Available</span>
            <span className="fast">ðŸŸ¡ Selling fast</span>
            <span className="sold">ðŸ”´ Sold out</span>
          </div>
        </div>

        <div className="cinema-list">
          {movie.screenings.map((screen) => (
            <div key={screen.cinema} className="cinema-block">
              <h4>{screen.cinema}</h4>
              <div className="times">
                {screen.times.map((time) => (
                  <button key={time} className="time-btn">
                    {time}
                  </button>
                ))}
              </div>
            </div>
          ))}
        </div>

        <button className="cinema-details-toggle">
          <span>ðŸŽ¬ Cinema Details</span>
          <ChevronDown size={16} />
        </button>
      </div>
    </div>
  );
};

export default MovieDetails;
