import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Import Axios
import { ChevronRight } from "lucide-react";
import "../styles/landing_page.css";

// MovieProps and HeroSlideProps interfaces...

const CinemaWebsite: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<string>("NOW SHOWING");
  const [currentSlide, setCurrentSlide] = useState<number>(0);
  const [showCinemaDropdown, setShowCinemaDropdown] = useState<boolean>(false);

  const [movies, setMovies] = useState<MovieProps[]>([]); // State to store movies data
  const [heroSlides, setHeroSlides] = useState<HeroSlideProps[]>([]); // State to store hero slides data
  const [cinemas, setCinemas] = useState<CinemaProps[]>([]); // State to store cinemas data

  // API call for movies and hero slides
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetching movies data from the backend
        const moviesResponse = await axios.get(
          "https://your-backend-url.com/api/movies"
        );
        setMovies(moviesResponse.data); // Assuming the response data is an array of movies

        // Fetching hero slides data from the backend
        const heroSlidesResponse = await axios.get(
          "https://your-backend-url.com/api/hero-slides"
        );
        setHeroSlides(heroSlidesResponse.data); // Assuming the response data is an array of hero slides

        // Fetching cinemas data from the backend
        const cinemasResponse = await axios.get(
          "https://your-backend-url.com/api/cinemas"
        );
        setCinemas(cinemasResponse.data); // Assuming the response data is an array of cinemas
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []); // Empty dependency array to run only on component mount

  // Auto-advance slideshow
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % heroSlides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [heroSlides.length]);

  const navigateToCinema = (cinemaId: string, movieId?: number) => {
    const path = `/showtimes/${cinemaId}${movieId ? `?movie=${movieId}` : ""}`;
    navigate(path);
  };

  const handleButtonClick = (path: string) => {
    navigate(path);
  };

  return (
    <div className="cinema-container">
      {/* Header */}
      <header className="header">{/* ... */}</header>

      {/* Hero Slideshow */}
      <section className="hero-slideshow">
        {heroSlides.map((slide, index) => (
          <div
            key={slide.id}
            className={`hero-slide ${index === currentSlide ? "active" : ""}`}
            style={{
              backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${slide.image})`,
            }}
          >
            <div className="hero-content">
              <h1>{slide.title}</h1>
              <p>{slide.subtitle}</p>
              <button
                className="hero-button"
                onClick={() => handleButtonClick(slide.buttonLink)}
              >
                {slide.buttonText}
              </button>
            </div>
          </div>
        ))}
      </section>

      {/* Movie Tabs */}
      <div className="movie-tabs-container">
        <div className="movie-tabs-wrapper">
          <div className="movie-tabs">
            {["NOW SHOWING", "BOOK EARLY", "COMING SOON", "GEMBIRAYA"].map(
              (tab) => (
                <button
                  key={tab}
                  className={`tab-button ${activeTab === tab ? "active" : ""}`}
                  onClick={() => setActiveTab(tab)}
                >
                  {tab}
                </button>
              )
            )}
          </div>
          <a href="/all-movies" className="view-all-link">
            VIEW ALL
            <span className="arrow-icon">→</span>
          </a>
        </div>

        {/* Movie Grid */}
        <div className="movie-grid">
          {movies.map((movie) => (
            <div key={movie.id} className="movie-card">
              {/* Movie Poster */}
              <div className="movie-poster">
                <img
                  src={movie.image || "/placeholder.svg"}
                  alt={movie.title}
                  className="poster-image"
                />

                {/* Tags */}
                {movie.tag && (
                  <div className={`movie-tag ${movie.tagColor}`}>
                    {movie.tag}
                  </div>
                )}

                {/* Release Date */}
                {movie.releaseDate && (
                  <div className="release-date">{movie.releaseDate}</div>
                )}

                {/* Rating */}
                <div
                  className={`movie-rating ${
                    movie.rating === "15" ? "rating-15" : "rating-p12"
                  }`}
                >
                  {movie.rating}
                </div>

                {/* Book Now Button */}
                {movie.hasBookNow && (
                  <div className="book-now-container">
                    <button
                      className="book-now-button"
                      onClick={() => navigateToCinema("1utama", movie.id)}
                    >
                      BOOK NOW
                    </button>
                  </div>
                )}
              </div>

              {/* Movie Title */}
              <div
                className="movie-title"
                onClick={() => navigateToCinema("1utama", movie.id)}
              >
                <h3>{movie.title}</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CinemaWebsite;
