import React from "react";
import "../users/styles/ticket_popup.css";
import { useNavigate } from "react-router-dom";

interface TicketPopUpProps {
  movieTitle: string;
  banner: string;
  cinemaName: string;
  roomName: string;
  date: string;
  time: string;
  seats: string[];
  pricePerTicket: number;
  onCancel: () => void;
}

const TicketConfirmationPopup: React.FC<TicketPopUpProps> = ({
  movieTitle,
  banner,
  cinemaName,
  roomName,
  date,
  time,
  seats,
  pricePerTicket,
  onCancel,
  remainingTime,
}) => {
  const totalPrice = pricePerTicket * seats.length;
  const navigate = useNavigate();

  const handleAddToCart = () => {
    const ticket = {
      movieTitle,
      banner,
      cinemaName,
      roomName,
      date,
      time,
      seats,
      pricePerTicket,
      totalPrice,
    };

    navigate("/checkout", { state: ticket });
  };

  return (
    <div className="popup-backdrop">
      <div className="popup-container">
        <div
          className="popup-banner"
          style={{ backgroundImage: `url(${banner})` }}
        >
          <div className="banner-overlay">
            <button className="popup-close-btn" onClick={onCancel}>
              ×
            </button>
            <h2 className="popup-movie-title">{movieTitle}</h2>
            <p>
              {cinemaName} {roomName}
            </p>
            <p>
              Date: {date} | Time: {time}
            </p>
          </div>
        </div>

        <div className="popup-body">
          <div className="ticket-info">
            <p>Seats: {seats.join(", ")}</p>
            <p>Number of Seats: {seats.length}</p>
            <p>
              <strong>Total Amount: RM {totalPrice.toFixed(2)}</strong>
            </p>
          </div>

          <div className="popup-controls">
            <button className="edit-button" onClick={onCancel}>
              ✏️ Edit Seat
            </button>
            <button className="confirm-button" onClick={handleAddToCart}>
              ADD TO CART – RM {totalPrice.toFixed(2)}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TicketConfirmationPopup;
