"use client"

import type React from "react"
import { useState, useEffect, useRef } from "react"
import { useNavigate, useLocation } from "react-router-dom"
import Navbar from "../../components/Navbar"
import Footer from "../../components/Footer"
import MovieCard from "../../components/MovieCards"
import MovieTabs from "../../components/MovieTabs"
import { getAllMovies } from "../../services/movieService"
import type { MovieProps } from "../../interfaces/movies"
import "../styles/movies_page.css"

const TABS: string[] = ["NOW SHOWING", "COMING SOON", "BOOK EARLY", "ALL"]
const GENRES: string[] = [
  "Action",
  "Adventure",
  "Comedy",
  "Drama",
  "Horror",
  "Sci-Fi",
  "Thriller",
  "Romance",
  "Animation",
  "Family",
]

const MoviesPage: React.FC = () => {
  const [activeTab, setActiveTab] = useState<string>("ALL")
  const [movies, setMovies] = useState<MovieProps[]>([])
  const [filteredMovies, setFilteredMovies] = useState<MovieProps[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [searchQuery, setSearchQuery] = useState("")
  const [selectedGenres, setSelectedGenres] = useState<string[]>([])
  const [showFilters, setShowFilters] = useState(false)
  const [sortBy, setSortBy] = useState<string>("newest")
  const [animateContent, setAnimateContent] = useState(false)

  const navigate = useNavigate()
  const location = useLocation()
  const filtersRef = useRef<HTMLDivElement>(null)

  useEffect(() => {
    // Check URL for category parameter
    const params = new URLSearchParams(location.search)
    const category = params.get("category")
    if (category) {
      // Convert URL format (e.g., NOW_SHOWING) to our format (NOW SHOWING)
      const formattedCategory = category.replace("_", " ")
      if (TABS.includes(formattedCategory)) {
        setActiveTab(formattedCategory)
      }
    }

    const fetchMovies = async () => {
      try {
        setLoading(true)
        const result = await getAllMovies()
        if (result.success) {
          setMovies(result.data)
        } else {
          setError(result.message || "Failed to load movies")
        }
      } catch (err) {
        setError("An error occurred while fetching movies")
        console.error(err)
      } finally {
        setLoading(false)
        // Trigger animation after data is loaded
        setTimeout(() => {
          setAnimateContent(true)
        }, 100)
      }
    }

    fetchMovies()
  }, [location.search])

  useEffect(() => {
    // Apply filters and sorting
    let result = [...movies]

    // Filter by tab/category
    if (activeTab !== "ALL") {
      result = result.filter((movie) => movie.category === activeTab)
    }

    // Filter by search query
    if (searchQuery) {
      const query = searchQuery.toLowerCase()
      result = result.filter(
        (movie) =>
          movie.title.toLowerCase().includes(query) || (movie.genre && movie.genre.toLowerCase().includes(query)),
      )
    }

    // Filter by selected genres
    if (selectedGenres.length > 0) {
      result = result.filter((movie) => {
        if (!movie.genre) return false
        return selectedGenres.some((genre) => movie.genre.toLowerCase().includes(genre.toLowerCase()))
      })
    }

    // Apply sorting
    switch (sortBy) {
      case "newest":
        result.sort((a, b) => {
          const dateA = a.releaseDate ? new Date(a.releaseDate).getTime() : 0
          const dateB = b.releaseDate ? new Date(b.releaseDate).getTime() : 0
          return dateB - dateA
        })
        break
      case "oldest":
        result.sort((a, b) => {
          const dateA = a.releaseDate ? new Date(a.releaseDate).getTime() : 0
          const dateB = b.releaseDate ? new Date(b.releaseDate).getTime() : 0
          return dateA - dateB
        })
        break
      case "a-z":
        result.sort((a, b) => a.title.localeCompare(b.title))
        break
      case "z-a":
        result.sort((a, b) => b.title.localeCompare(a.title))
        break
      default:
        break
    }

    setFilteredMovies(result)
  }, [movies, activeTab, searchQuery, selectedGenres, sortBy])

  useEffect(() => {
    // Close filters dropdown when clicking outside
    const handleClickOutside = (event: MouseEvent) => {
      if (filtersRef.current && !filtersRef.current.contains(event.target as Node)) {
        setShowFilters(false)
      }
    }

    document.addEventListener("mousedown", handleClickOutside)
    return () => {
      document.removeEventListener("mousedown", handleClickOutside)
    }
  }, [])

  const handleTabChange = (tab: string) => {
    setActiveTab(tab)
    // Update URL without reloading the page
    const formattedCategory = tab.replace(" ", "_")
    navigate(`/movies${tab !== "ALL" ? `?category=${formattedCategory}` : ""}`, { replace: true })
  }

  const toggleGenre = (genre: string) => {
    if (selectedGenres.includes(genre)) {
      setSelectedGenres(selectedGenres.filter((g) => g !== genre))
    } else {
      setSelectedGenres([...selectedGenres, genre])
    }
  }

  const clearFilters = () => {
    setSearchQuery("")
    setSelectedGenres([])
    setSortBy("newest")
  }

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loader"></div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error}</p>
        <button onClick={() => window.location.reload()} className="retry-button">
          Try Again
        </button>
      </div>
    )
  }

  return (
    <div className="movies-page">
      <Navbar />

      <div className="movies-hero">
        <div className="hero-content">
          <h1>Explore Movies</h1>
          <p>Discover the latest blockbusters, upcoming releases, and timeless classics</p>

          <div className="search-container">
            <input
              type="text"
              placeholder="Search for movies..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button className="search-button">
              <span className="search-icon">üîç</span>
            </button>
          </div>
        </div>
      </div>

      <div className="movies-content">
        <div className={`movies-filters-container ${animateContent ? "animate" : ""}`}>
          <div className="tabs-container">
            <MovieTabs tabs={TABS} activeTab={activeTab} onTabChange={handleTabChange} />
          </div>

          <div className="filters-actions">
            <div className="filter-dropdown" ref={filtersRef}>
              <button className="filter-button" onClick={() => setShowFilters(!showFilters)}>
                <span className="filter-icon">üîç</span>
                Filters {selectedGenres.length > 0 && `(${selectedGenres.length})`}
              </button>

              {showFilters && (
                <div className="filters-dropdown-content">
                  <h3>Genre</h3>
                  <div className="genre-filters">
                    {GENRES.map((genre) => (
                      <label key={genre} className="genre-checkbox">
                        <input
                          type="checkbox"
                          checked={selectedGenres.includes(genre)}
                          onChange={() => toggleGenre(genre)}
                        />
                        <span className="checkbox-label">{genre}</span>
                      </label>
                    ))}
                  </div>

                  <div className="filter-actions">
                    <button className="clear-filters" onClick={clearFilters}>
                      Clear All
                    </button>
                    <button className="apply-filters" onClick={() => setShowFilters(false)}>
                      Apply
                    </button>
                  </div>
                </div>
              )}
            </div>

            <div className="sort-dropdown">
              <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="sort-select">
                <option value="newest">Newest First</option>
                <option value="oldest">Oldest First</option>
                <option value="a-z">A-Z</option>
                <option value="z-a">Z-A</option>
                <option value="rating">Highest Rated</option>
              </select>
            </div>
          </div>
        </div>

        <div className={`movies-results ${animateContent ? "animate" : ""}`}>
          <div className="results-info">
            <span className="results-count">
              {filteredMovies.length} {filteredMovies.length === 1 ? "movie" : "movies"} found
            </span>

            {(searchQuery || selectedGenres.length > 0) && (
              <button className="clear-search" onClick={clearFilters}>
                Clear Filters
              </button>
            )}
          </div>

          {filteredMovies.length > 0 ? (
            <div className="movies-grid">
              {filteredMovies.map((movie) => (
                <MovieCard key={movie.movieId} movie={movie} />
              ))}
            </div>
          ) : (
            <div className="no-results">
              <div className="no-results-icon">üé¨</div>
              <h3>No movies found</h3>
              <p>Try adjusting your filters or search criteria</p>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </div>
  )
}

export default MoviesPage
