// src/components/ScheduleControls.tsx
import React from "react";
import "../admin/styles/ScheduleControls.css";
import { MovieProps, CinemaProps } from "../data/mockData";

interface ScheduleControlsProps {
  selectedMovie: string;
  setSelectedMovie: (value: string) => void;
  selectedLocation: string;
  setSelectedLocation: (value: string) => void;
  selectedDate: string;
  setSelectedDate: (value: string) => void;
  selectedTime: string;
  setSelectedTime: (value: string) => void;
  movies: MovieProps[];
  cinemas: CinemaProps[];
}

const ScheduleControls: React.FC<ScheduleControlsProps> = ({
  selectedMovie,
  setSelectedMovie,
  selectedLocation,
  setSelectedLocation,
  selectedDate,
  setSelectedDate,
  selectedTime,
  setSelectedTime,
  movies,
  cinemas,
}) => {
  // Get unique time slots from all screenings
  const getTimeSlots = () => {
    const allTimes = new Set<string>();
    movies.forEach(movie => {
      movie.screenings.forEach(screening => {
        screening.sessions.forEach(session => {
          allTimes.add(session.time);
        });
      });
    });
    return Array.from(allTimes).map(time => ({
      id: time,
      name: formatTime(time)
    }));
  };

  // Format time from "13:00" to "1:00 PM"
  const formatTime = (timeString: string) => {
    const [hours, minutes] = timeString.split(':');
    const hourNum = parseInt(hours);
    const period = hourNum >= 12 ? 'PM' : 'AM';
    const displayHour = hourNum % 12 || 12;
    return `${displayHour}:${minutes} ${period}`;
  };

  // Get available dates for selected movie and location
  const getAvailableDates = () => {
    if (!selectedMovie || !selectedLocation) return [];
    
    const movie = movies.find(m => m.id.toString() === selectedMovie);
    if (!movie) return [];

    const screening = movie.screenings.find(s => s.cinema === selectedLocation);
    if (!screening) return [];

    return screening.sessions.map(session => session.date);
  };

  // Get available times for selected movie, location and date
  const getAvailableTimes = () => {
    if (!selectedMovie || !selectedLocation || !selectedDate) return [];
    
    const movie = movies.find(m => m.id.toString() === selectedMovie);
    if (!movie) return [];

    const screening = movie.screenings.find(s => s.cinema === selectedLocation);
    if (!screening) return [];

    return screening.sessions
      .filter(session => session.date === selectedDate)
      .map(session => ({
        id: session.time,
        name: formatTime(session.time),
        status: session.status
      }));
  };

  return (
    <div className="schedule-controls">
      {/* Movie Selection */}
      <select
        value={selectedMovie}
        onChange={(e) => {
          setSelectedMovie(e.target.value);
          setSelectedLocation("");
          setSelectedDate("");
          setSelectedTime("");
        }}
      >
        <option value="">Select Movie</option>
        {movies.map((movie) => (
          <option key={movie.id} value={movie.id}>
            {movie.title}
          </option>
        ))}
      </select>

      {/* Location Selection */}
      <select
        value={selectedLocation}
        onChange={(e) => {
          setSelectedLocation(e.target.value);
          setSelectedDate("");
          setSelectedTime("");
        }}
        disabled={!selectedMovie}
      >
        <option value="">Select Location</option>
        {selectedMovie && 
          Array.from(new Set(
            movies
              .find(m => m.id.toString() === selectedMovie)
              ?.screenings.map(s => s.cinema) || []
          )).map(location => (
            <option key={location} value={location}>
              {location}
            </option>
          ))
        }
      </select>

      {/* Date Selection */}
      <select
        value={selectedDate}
        onChange={(e) => {
          setSelectedDate(e.target.value);
          setSelectedTime("");
        }}
        disabled={!selectedLocation}
      >
        <option value="">Select Date</option>
        {getAvailableDates().map(date => (
          <option key={date} value={date}>
            {new Date(date).toLocaleDateString('en-US', {
              weekday: 'short',
              month: 'short',
              day: 'numeric'
            })}
          </option>
        ))}
      </select>

      {/* Time Selection */}
      <select
        value={selectedTime}
        onChange={(e) => setSelectedTime(e.target.value)}
        disabled={!selectedDate}
      >
        <option value="">Select Time</option>
        {getAvailableTimes().map(time => (
          <option 
            key={time.id} 
            value={time.id}
            disabled={time.status === "sold out"}
            className={time.status === "sold out" ? "sold-out" : ""}
          >
            {time.name} {time.status === "sold out" ? "(Sold Out)" : ""}
          </option>
        ))}
      </select>
    </div>
  );
};

export default ScheduleControls;
