import React from "react";

interface PosterUploadProps {
  poster: string | null;
  setPoster: React.Dispatch<React.SetStateAction<string | null>>;
}

const PosterUpload: React.FC<PosterUploadProps> = ({ poster, setPoster }) => {
  const handleLinkChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setPoster(e.target.value);
  };

  return (
    <div className="movie-poster">
      <div className="poster-upload">
        <label htmlFor="poster-link">Paste link here:</label>
        <input
          id="poster-link"
          type="text"
          placeholder="https://example.com/poster.jpg"
          value={poster || ""}
          onChange={handleLinkChange}
        />

        <div className="poster-preview-box">
          {poster ? (
            <img
              src={poster}
              alt="Movie Poster"
              onError={(e) => (e.currentTarget.src = "https://via.placeholder.com/200x300?text=Invalid+Link")}
            />
          ) : (
            <p style={{ color: "#888" }}>Image preview will appear here</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default PosterUpload;
