import { FC } from "react";
import { SeatProps as SeatData } from "../interfaces/seats"; // alias to avoid name clash
import { ReactComponent as SeatIcon } from "public/assets/icons/seat.svg";

interface SeatComponentProps {
  seat: SeatData;
  isSelected: boolean;
  onClick: () => void;
}

const Seat: FC<SeatComponentProps> = ({ seat, isSelected, onClick }) => {
  const getColor = () => {
    if (seat.status === "sold") return "#999"; // grey
    if (isSelected) return "#00C896"; // green
    return "#fff"; // default white
  };

  return (
    <div className="seat-wrapper" onClick={onClick}>
      <SeatIcon style={{ fill: getColor(), width: 30, height: 30 }} />
    </div>
  );
};

export default Seat;
