"use client";
import {
  ChevronDown,
  ChevronLeft,
  ChevronUp,
  Heart,
  SlidersHorizontal,
  Info,
} from "lucide-react";
import { useParams, useNavigate } from "react-router-dom";
// Removed next/image import as it is not supported in this project
import { mockMovies } from "../data/mockData";
import "../styles/moviedetails.css";

const MovieDetails: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const movie = mockMovies.find((m) => m.id === Number(id));

  if (!movie) {
    return <div className="text-white p-8">Movie not found.</div>;
  }

  return (
    <div className="movie-container">
      {/* Background Image */}
      <div className="background-image">
        <img
          src={movie.banner}
          alt="Movie background"
          className="background-image"
        />
      </div>

      {/* Main Content */}
      <div className="content">
        {/* Back Button */}
        <button className="back-button" onClick={() => navigate(-1)}>
          <ChevronLeft className="h-5 w-5" />
          BACK
        </button>

        {/* Movie Info */}
        <div className="movie-info">
          <h1 className="movie-title">{movie.title}</h1>
          <div className="movie-meta">
            <div className="movie-category">
              <span className="movie-category-icon">🎬</span>
              <span>{movie.genre}</span>
            </div>
            <span className="divider">|</span>
            <span>{movie.duration}</span>
            <span className="divider">|</span>
            <span>{movie.languages?.join(", ")}</span>
          </div>

          <div className="action-buttons">
            <button className="info-button">
              More info <ChevronDown className="h-4 w-4" />
            </button>
            <button className="trailer-button">Watch trailer</button>
          </div>
        </div>

        {/* Booking Section */}
        <div className="booking-section">
          {/* Date Selection */}
          <div className="date-selection">
            {movie.dates?.map((date, index) => (
              <div key={date} className="date-option">
                <span className="date-label">
                  {new Date(date).toLocaleDateString("en-US", {
                    weekday: "short",
                  })}
                </span>
                <span className="date-value">
                  {new Date(date).toLocaleDateString("en-US", {
                    day: "2-digit",
                    month: "short",
                  })}
                </span>
                {index === 0 && <div className="date-indicator"></div>}
              </div>
            ))}
          </div>

          {/* Cinema Info */}
          <div className="location-section">
            <div className="location-dropdown">
              <span>Sarawak</span>
              <ChevronUp className="h-4 w-4" />
            </div>
            <div className="current-location">
              <span>Sarawak - {movie.screenings?.[0]?.cinema}</span>
            </div>

            <div className="status-indicators">
              <div className="status-badge">
                <span className="status-icon available-icon">✓</span>
                <span className="available-text">Available</span>
              </div>
              <div className="status-badge">
                <span className="status-icon selling-icon" />
                <span className="selling-text">Selling fast</span>
              </div>
              <div className="status-badge">
                <span className="status-icon soldout-icon" />
                <span className="soldout-text">Sold out</span>
              </div>
              <button className="filter-button">
                <SlidersHorizontal className="h-4 w-4" />
                FILTER BY
              </button>
            </div>
          </div>

          {/* Cinema Grid */}
          <div className="cinema-grid">
            <div className="cinema-list">
              {movie.screenings?.map((screen) => (
                <div key={screen.cinema} className="cinema-item">
                  <div className="cinema-name">
                    <Heart className="h-5 w-5 text-white" />
                    <span className="cinema-name-text">{screen.cinema}</span>
                  </div>
                </div>
              ))}
            </div>

            <div className="showtimes">
              {movie.screenings?.map((screen) => (
                <div key={screen.cinema} className="showtime-category">
                  <div className="category-header">
                    <span className="category-name">Deluxe</span>
                    <div className="info-icon">
                      <Info className="h-3 w-3" />
                    </div>
                  </div>
                  <div className="showtime-list">
                    {screen.times.map((time) => (
                      <button key={time} className="showtime-button">
                        {time}
                      </button>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Cinema Details */}
        <div className="cinema-details">
          <button className="details-button">
            <div className="details-label">
              <span className="details-icon">🎬</span>
              <span>Cinema Details</span>
            </div>
            <ChevronDown className="h-5 w-5" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default MovieDetails;
