import React, { useEffect, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { Ticket } from "../../interfaces/ticket";
import "../styles/checkout_page.css";

const CheckoutPage: React.FC = () => {
  const { state } = useLocation();
  const navigate = useNavigate();

  const [timer, setTimer] = useState(300); // 5-minute countdown
  const [isProcessing, setIsProcessing] = useState(false);
  const [ticket, setTicket] = useState<Ticket | null>(null);

  const {
    movieTitle,
    banner,
    cinemaName,
    roomName,
    date,
    time,
    seats = [], // default to empty array
    language,
    genre,
    rating,
    duration,
    pricePerTicket = 18, // fallback price
  } = state || {};

  const totalPrice = seats.length * pricePerTicket;

  useEffect(() => {
    if (!timer || ticket) return;
    const interval = setInterval(() => {
      setTimer((prev) => (prev > 0 ? prev - 1 : 0));
    }, 1000);
    return () => clearInterval(interval);
  }, [timer, ticket]);

  useEffect(() => {
    console.log("📦 Ticket received:", ticket);
  }, [ticket]);

  const formatTime = (sec: number) => {
    const m = Math.floor(sec / 60)
      .toString()
      .padStart(2, "0");
    const s = (sec % 60).toString().padStart(2, "0");
    return `${m}:${s}`;
  };

  const handleConfirm = () => {
    setIsProcessing(true);
    setTimeout(() => {
      const newTicket: Ticket = {
        ticketId: Math.random().toString(36).substring(2),
        name: "John Doe",
        email: "john@example.com",
        movieTitle,
        cinemaName,
        roomName,
        date,
        time,
        seats,
        language,
        genre,
        rating,
        duration,
        bookedAt: new Date().toISOString(),
      };
      setTicket(newTicket);
      setIsProcessing(false);
      navigate("/confirmation", { state: { ticket: newTicket } });
    }, 2000);
  };

  if (!state) return <p>No booking information provided.</p>;

  return (
    <div className="checkout-wrapper">
      {/* 🎬 Banner */}
      <div
        className="checkout-banner"
        style={{ backgroundImage: `url(${banner})` }}
      >
        <div className="banner-overlay">
          <h1 className="checkout-movie-title">{movieTitle}</h1>
          <p>
            {cinemaName} | {roomName}
          </p>
          <p>
            {date} at {time}
          </p>
        </div>
      </div>

      {/* 🧾 Summary */}
      <div className="checkout-body">
        <h2>Booking Summary</h2>
        <p>
          <strong>Seats:</strong> {seats.join(", ")}
        </p>
        <p>
          <strong>Tickets:</strong> {seats.length}
        </p>
        <p>
          <strong>Total:</strong> RM {totalPrice.toFixed(2)}
        </p>
        <p className="countdown">⏳ Time Left: {formatTime(timer)}</p>

        <button
          className="confirm-button"
          onClick={handleConfirm}
          disabled={isProcessing}
        >
          {isProcessing
            ? "Processing..."
            : `Confirm & Pay RM ${totalPrice.toFixed(2)}`}
        </button>
      </div>
    </div>
  );
};

export default CheckoutPage;
