import React from "react";
import { mockMovies } from "../data/mockData";

interface PosterSliderProps {
  filter: string;
}

const PosterSlider: React.FC<PosterSliderProps> = ({ filter }) => {
  // Function to filter movies based on category (e.g., "today", "upcoming", "out-dated")
  const filteredMovies = mockMovies.filter((movie) => {
    if (filter === "today") {
      return new Date(movie.releaseDate).toDateString() === new Date().toDateString();
    } else if (filter === "upcoming") {
      return new Date(movie.releaseDate) > new Date();
    } else if (filter === "out-dated") {
      return new Date(movie.releaseDate) < new Date();
    }
    return true;
  });

  return (
    <div className="poster-slider">
      <h2 className="text-xl font-semibold mb-2">ðŸŽ¥ Movie Posters</h2>
      <div className="flex gap-4 overflow-x-auto">
        {filteredMovies.map((movie) => (
          <div key={movie.id} className="poster-item">
            <img src={movie.image} alt={movie.title} className="poster-img" />
            <h3 className="text-center mt-2">{movie.title}</h3>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PosterSlider;
