import React, { useState } from "react";
import "../styles/pages_styles.css";
import InputField from "../components/InputField";
import { register } from "../services/authService";

const RegisterPage: React.FC = () => {
    const [userId, setUserId] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [error, setError] = useState("");
    const [successMessage, setSuccessMessage] = useState("");

    const handleRegister = async () => {
        if (password !== confirmPassword) {
            setError("Passwords do not match");
            return;
        }

        const result = await register(userId, password);

        if (result.success) {
            console.log("Registration Successful:", result.data);
            setSuccessMessage("Registration successful! You can now log in.");
            setError("");
        } else {
            setError("Registration Failed");
            console.log("Registration Failed:", result.message);
        }
    };

    return (
        <div className="container">
            <div className="login-box">
                <h2 className="title">User Registration</h2>
                {error && <p className="error-message">{error}</p>}
                {successMessage && <p className="success-message">{successMessage}</p>}
                <InputField label="User ID" type="text" value={userId} onChange={setUserId} />
                <InputField label="Password" type="password" value={password} onChange={setPassword} />
                <InputField label="Confirm Password" type="password" value={confirmPassword} onChange={setConfirmPassword} />
                <button onClick={handleRegister} className="login-button">
                    Register
                </button>
            </div>
        </div>
    );
};

export default RegisterPage;
