// components/DateTabBar.tsx
import React from "react";
import "../users/styles/date_tab_bar.css"; // or wherever your styles live

export interface DateTabBarProps {
  availableDates: string[];
  selectedDate: string;
  onDateSelect: (date: string) => void;
}

const DateTabBar: React.FC<DateTabBarProps> = ({
  availableDates,
  selectedDate,
  onDateSelect,
}) => {
  return (
    <div className="date-tab-bar">
      {availableDates.map((date) => {
        const label = new Date(date).toLocaleDateString("en", {
          weekday: "short",
          day: "numeric",
          month: "short",
        });

        return (
          <div
            key={date}
            className={`date-tab ${selectedDate === date ? "active" : ""}`}
            onClick={() => onDateSelect(date)}
          >
            <span className="date-text">{label.toUpperCase()}</span>
          </div>
        );
      })}
    </div>
  );
};

export default DateTabBar;
