import React, { useState } from "react";
import "../styles/seat_booking.css";
import { mockMovies } from "../../data/mockData";
const selectedMovie = mockMovies[1]; // e.g. THE MARTIAN (replace with routing logic later)

const SeatBookingPage: React.FC = () => {
  const [selectedTime, setSelectedTime] = useState("6:00 PM");
  const [selectedSeats, setSelectedSeats] = useState<string[]>([]);

  const showtimes = [
    { time: "11:45 AM", date: "12 Apr 2025" },
    { time: "2:00 PM", date: "12 Apr 2025" },
    { time: "6:00 PM", date: "12 Apr 2025" },
    { time: "8:00 PM", date: "12 Apr 2025" },
    { time: "12:10 AM", date: "13 Apr 2025" },
  ];

  const rows = ["A", "B", "C", "D", "E"];
  const seatsPerRow = 10;
  const soldSeats = ["E1", "E2"];

  const handleSeatClick = (seat: string) => {
    if (soldSeats.includes(seat)) return;
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getSeatStatus = (seat: string) => {
    if (selectedSeats.includes(seat)) return "selected";
    if (soldSeats.includes(seat)) return "sold";
    return "available";
  };

  return (
    <div className="seat-booking-wrapper">
      {/* üé¨ Banner Section with Gradient Fade */}
      <div className="banner-section">
        <div className="banner-overlay">
          <div className="booking-header">
            <div className="header-inner">
              <button className="back-button">‚Üê</button>

              <div className="header-center">
                <div className="progress-indicator">
                  <div className="progress-step active">1</div>
                  <div className="progress-line"></div>
                  <div className="progress-step inactive">2</div>
                </div>
              </div>

              <button className="close-button">√ó</button>
            </div>
          </div>

          <section className="movie-info">
            <h1 className="movie-title">BLOOD BROTHERS: BARA NAGA</h1>
            <p className="movie-meta">
              Action, Drama | 2 hr 9 mins | Bahasa Melayu
            </p>
            <div className="movie-details">
              <span className="detail-tag">1 UTAMA</span>
              <span className="detail-tag">INDULGE 1</span>
              <span className="detail-tag">12 Apr 2025, {selectedTime}</span>
            </div>
            <h3 className="showtimes-label">Available showtimes</h3>
            <div className="showtimes-container">
              {showtimes.map((show) => (
                <button
                  key={show.time}
                  className={`showtime-button ${
                    selectedTime === show.time ? "active" : ""
                  }`}
                  onClick={() => setSelectedTime(show.time)}
                >
                  <div>{show.time}</div>
                  {show.date !== "12 Apr 2025" && (
                    <div className="showtime-date">{show.date}</div>
                  )}
                </button>
              ))}
            </div>
          </section>
        </div>
      </div>

      {/* üéüÔ∏è Booking Body Section */}
      <main className="booking-body">
        <section className="legend">
          <div className="legend-item">
            <div className="legend-indicator selected" />
            Selected
          </div>
          <div className="legend-item">
            <div className="legend-indicator sold" />
            Sold
          </div>
          <div className="legend-item">
            <div className="legend-indicator single" />
            Single
          </div>
          <div className="legend-item">
            <div className="legend-indicator twin">
              <div className="twin-divider" />
            </div>
            Twin
          </div>
        </section>

        <div className="screen-container">
          <img
            src="/assets/icons/screen.jpg" // replace with actual path
            alt="Cinema Screen"
            className="screen-img"
          />
          <div className="screen-label">SCREEN</div>
        </div>

        <section className="seats-container">
          <div className="theater-layout">
            <div className="row-labels">
              {rows.map((r) => (
                <div key={`L-${r}`} className="row-label">
                  {r}
                </div>
              ))}
            </div>
            <div className="seats-grid">
              {rows.flatMap((row) =>
                Array.from({ length: seatsPerRow }, (_, i) => {
                  const id = `${row}${i + 1}`;
                  return (
                    <button
                      key={id}
                      className={`seat ${getSeatStatus(id)}`}
                      onClick={() => handleSeatClick(id)}
                      disabled={getSeatStatus(id) === "sold"}
                    >
                      <div className="seat-inner">
                        {i % 2 === 0 && <div className="seat-divider" />}
                      </div>
                    </button>
                  );
                })
              )}
            </div>
            <div className="row-labels">
              {rows.map((r) => (
                <div key={`R-${r}`} className="row-label">
                  {r}
                </div>
              ))}
            </div>
          </div>
        </section>

        <div className="book-button-container">
          <button className="book-button" disabled={selectedSeats.length === 0}>
            BOOK SEATS!
          </button>
        </div>
      </main>
    </div>
  );
};

export default SeatBookingPage;
