import React, { useState, useEffect } from "react";
import "..//ManageShow.css";

interface Movie {
  id: number;
  title: string;
  poster: string;
  duration: number; // in minutes
  releaseDate: string;
  endDate: string;
}

interface Location {
  id: number;
  name: string;
  cinemas: Cinema[];
}

interface Cinema {
  id: number;
  name: string;
  halls: string[];
}

const ManageShow: React.FC = () => {
  const [movies, setMovies] = useState<Movie[]>([]);
  const [locations, setLocations] = useState<Location[]>([]);
  const [selectedMovie, setSelectedMovie] = useState<Movie | null>(null);
  const [selectedLocation, setSelectedLocation] = useState<Location | null>(null);
  const [selectedCinema, setSelectedCinema] = useState<Cinema | null>(null);
  const [startDate, setStartDate] = useState<string>("");
  const [endDate, setEndDate] = useState<string>("");
  const [selectedTimes, setSelectedTimes] = useState<string[]>([]);

  useEffect(() => {
    setMovies([
      { id: 1, title: "Movie A", poster: "poster1.jpg", duration: 150, releaseDate: "2024-03-20", endDate: "2024-04-10" },
      { id: 2, title: "Movie B", poster: "poster2.jpg", duration: 120, releaseDate: "2024-03-25", endDate: "2024-04-15" }
    ]);

    setLocations([
      {
        id: 1,
        name: "Kuala Lumpur",
        cinemas: [
          { id: 1, name: "KL Cinema 1", halls: ["Hall A", "Hall B"] },
          { id: 2, name: "KL Cinema 2", halls: ["Hall C", "Hall D"] }
        ]
      }
    ]);
  }, []);

  const generateTimeSlots = () => {
    if (!selectedMovie) return [];
    const duration = selectedMovie.duration;
    const cleaningTime = 15;
    const slots = [];
    let currentTime = 0;

    while (currentTime + duration + cleaningTime <= 24 * 60) {
      const startHour = String(Math.floor(currentTime / 60)).padStart(2, "0");
      const startMinutes = String(currentTime % 60).padStart(2, "0");

      currentTime += duration;
      const endHour = String(Math.floor(currentTime / 60)).padStart(2, "0");
      const endMinutes = String(currentTime % 60).padStart(2, "0");

      slots.push(`${startHour}:${startMinutes} - ${endHour}:${endMinutes}`);
      currentTime += cleaningTime;
    }
    return slots;
  };

  const toggleTimeSelection = (time: string) => {
    setSelectedTimes(prev => prev.includes(time) ? prev.filter(t => t !== time) : [...prev, time]);
  };

  const selectAllTimes = () => setSelectedTimes(generateTimeSlots());
  const removeAllTimes = () => setSelectedTimes([]);

  return (
    <div className="manage-show">
      <h1>Manage Showtime</h1>

      <div className="movie-selection">
        <h2>Select a Movie</h2>
        {movies.map((movie) => (
          <button key={movie.id} className={selectedMovie?.id === movie.id ? "selected" : ""} onClick={() => setSelectedMovie(movie)}>
            {movie.title}
          </button>
        ))}
      </div>

      {selectedMovie && (
        <>
          <div className="location-selection">
            <h2>Select a Location</h2>
            <select onChange={(e) => setSelectedLocation(locations[parseInt(e.target.value)])}>
              <option value="">Select Location</option>
              {locations.map((loc, index) => (
                <option key={loc.id} value={index}>{loc.name}</option>
              ))}
            </select>
          </div>

          {selectedLocation && (
            <div className="cinema-selection">
              <h2>Select a Cinema</h2>
              <select onChange={(e) => setSelectedCinema(selectedLocation.cinemas[parseInt(e.target.value)])}>
                <option value="">Select Cinema</option>
                {selectedLocation.cinemas.map((cinema, index) => (
                  <option key={cinema.id} value={index}>{cinema.name}</option>
                ))}
              </select>
            </div>
          )}

          {selectedCinema && (
            <div className="date-selection">
              <h2>Select Schedule Dates</h2>
              <input type="date" min={selectedMovie.releaseDate} max={selectedMovie.endDate} onChange={(e) => setStartDate(e.target.value)} />
              <input type="date" min={startDate} max={selectedMovie.endDate} onChange={(e) => setEndDate(e.target.value)} />
            </div>
          )}

          {endDate && (
            <div className="time-selection">
              <h2>Select Time Slots</h2>
              <button onClick={selectAllTimes}>Select All</button>
              <button onClick={removeAllTimes}>Remove All</button>
              <div className="time-options">
                {generateTimeSlots().map((slot) => (
                  <button key={slot} className={selectedTimes.includes(slot) ? "selected" : ""} onClick={() => toggleTimeSelection(slot)}>
                    {slot}
                  </button>
                ))}
              </div>
            </div>
          )}

          <button disabled={selectedTimes.length === 0} className="add-showtime-btn">Add Showtime</button>
        </>
      )}
    </div>
  );
};

export default ManageShow;
