export function generateMockSeats(
  movieId: number,
  cinema: string,
  date: string,
  time: string,
  sold: number = 0
) {
  const total = 36;
  const rows = "ABCDEF";
  const seats = [];
  let count = 0;

  for (const row of rows) {
    for (let num = 1; num <= 6; num++) {
      seats.push({
        id: `${row}${num}`,
        row,
        number: num,
        status: count < sold ? "sold" : "available",
      });
      count++;
    }
  }

  return {
    movieId,
    cinema,
    date,
    time,
    seats,
  };
}
