import React, { useState } from "react";
import "../styles/cinemas_page.css";
import { Heart, ChevronDown, User, Filter, Info, Play } from "lucide-react";
import Navbar from "../../components/Navbar";
import Footer from "../../components/Footer";
import DateTabBar from "../../components/DateTab";
import { mockMovies } from "../../data/mockData";

export default function CinemaPage() {
  const uniqueCinemas = Array.from(
    new Set(
      mockMovies.flatMap(
        (movie) => movie.screenings?.map((s) => s.cinema) || []
      )
    )
  );
  const [selectedDate, setSelectedDate] = useState("14 APR");
  const [locationDropdownOpen, setLocationDropdownOpen] = useState(false);

  const dates = [
    { day: "TODAY", date: "14 APR" },
    { day: "TUE", date: "15 APR" },
    { day: "WED", date: "16 APR" },
    { day: "THU", date: "17 APR" },
    { day: "FRI", date: "18 APR" },
    { day: "SAT", date: "19 APR" },
    { day: "SUN", date: "20 APR" },
    { day: "SAT", date: "26 APR" },
    { day: "SUN", date: "27 APR" },
    { day: "SAT", date: "3 MAY" },
  ];

  const locations = uniqueCinemas;

  const movies = [
    {
      id: 1,
      title: "BLOOD BROTHERS: BARA NAGA",
      image: "/placeholder.svg?height=200&width=150",
      details: "Action, Drama | 2 hr 9 mins | Bahasa Melayu",
      showtimes: [
        { type: "INDULGE", times: ["8:00 PM"] },
        { type: "BEANIE", times: ["8:45 PM"] },
        {
          type: "DELUXE",
          times: ["6:45 PM", "7:00 PM", "9:30 PM", "9:45 PM", "10:35 PM"],
        },
      ],
    },
    {
      id: 2,
      title: "GOOD BAD UGLY (TAMIL)",
      image: "/placeholder.svg?height=200&width=150",
      details: "Action, Drama | 2 hr 20 mins | Tamil",
      showtimes: [
        { type: "INDULGE", times: ["9:00 PM"] },
        { type: "DELUXE", times: ["8:00 PM", "10:15 PM"] },
      ],
    },
  ];

  const experienceTypes = [
    { id: "all", label: "ALL" },
    { id: "indulge", label: "INDULGE" },
    { id: "indulge-family", label: "INDULGE FAMILY PLUS" },
    { id: "imax", label: "IMAX" },
    { id: "beanie", label: "BEANIE" },
    { id: "junior", label: "JUNIOR" },
    { id: "family", label: "FAMILY" },
    { id: "atmos", label: "ATMOS" },
    { id: "infinity", label: "INFINITY" },
    { id: "onyx", label: "ONYX" },
  ];

  const [selectedExperience, setSelectedExperience] = useState("all");

  return (
    <div className="movie-theater">
      <div className="experience-types">
        {experienceTypes.map((type) => (
          <button
            key={type.id}
            className={`experience-btn ${
              selectedExperience === type.id ? "active" : ""
            }`}
            onClick={() => setSelectedExperience(type.id)}
          >
            {type.label}
          </button>
        ))}
      </div>
      {/* <DateTabBar
        availableDates={availableDates}
        selectedDate={selectedDate}
        onDateSelect={setSelectedDate}
      /> */}

      <div className="content">
        <div className="sidebar">
          <div className="location-list">
            {locations.map((location) => (
              <div
                key={location}
                className={`location-item ${
                  selectedLocation === location ? "selected" : ""
                }`}
                onClick={() => setSelectedLocation(location)}
              >
                <Heart
                  size={16}
                  className={`heart-icon ${
                    selectedLocation === location
                      ? "location-heart-selected"
                      : ""
                  }`}
                />
                <span>{location}</span>
              </div>
            ))}
          </div>
        </div>

        <div className="main-content">
          <div className="location-header">
            <h2>selected location hrere</h2>
            <div className="status-indicators">
              <span className="status available">Available</span>
              <span className="status selling-fast">Selling fast</span>
              <span className="status sold-out">Sold out</span>
            </div>
            <button className="filter-btn">
              <Filter size={16} /> FILTER BY
            </button>
          </div>

          <div className="movies-list">
            {movies.map((movie) => (
              <div key={movie.id} className="movie-card">
                <div className="movie-poster">
                  <img
                    src={movie.image || "/placeholder.svg"}
                    alt={movie.title}
                  />
                </div>
                <div className="movie-details">
                  <h3 className="movie-title">{movie.title}</h3>
                  <p className="movie-info">{movie.details}</p>
                  {movie.showtimes.map((showtime) => (
                    <div key={showtime.type} className="showtime-group">
                      <div className="showtime-type">
                        {showtime.type} <span className="info-circle">i</span>
                      </div>
                      <div className="showtime-buttons">
                        {showtime.times.map((time) => (
                          <button key={time} className="time-btn">
                            {time}
                          </button>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}
