// src/components/ScheduleControls.tsx
import React from "react";
import "../admin/styles/ScheduleControls.css";

interface ScheduleControlsProps {
  selectedMovie: string;
  setSelectedMovie: (value: string) => void;
  selectedLocation: string;
  setSelectedLocation: (value: string) => void;
  selectedDate: string;
  setSelectedDate: (value: string) => void;
  selectedTime: string;
  setSelectedTime: (value: string) => void;
}

const ScheduleControls: React.FC<ScheduleControlsProps> = ({
  selectedMovie,
  setSelectedMovie,
  selectedLocation,
  setSelectedLocation,
  selectedDate,
  setSelectedDate,
  selectedTime,
  setSelectedTime,
}) => {
  // You can fetch these from your API or context
  const movies = [
    { id: "movie1", name: "Movie 1" },
    { id: "movie2", name: "Movie 2" },
  ];

  const locations = [
    { id: "location1", name: "Main Theater" },
    { id: "location2", name: "VIP Lounge" },
  ];

  const timeSlots = [
    { id: "12:00", name: "12:00 PM" },
    { id: "15:00", name: "3:00 PM" },
    { id: "18:00", name: "6:00 PM" },
  ];

  return (
    <div className="schedule-controls">
      <select
        value={selectedMovie}
        onChange={(e) => setSelectedMovie(e.target.value)}
      >
        <option value="">Select Movie</option>
        {movies.map((movie) => (
          <option key={movie.id} value={movie.id}>
            {movie.name}
          </option>
        ))}
      </select>

      <select
        value={selectedLocation}
        onChange={(e) => setSelectedLocation(e.target.value)}
      >
        <option value="">Select Location</option>
        {locations.map((location) => (
          <option key={location.id} value={location.id}>
            {location.name}
          </option>
        ))}
      </select>

      <input
        type="date"
        value={selectedDate}
        onChange={(e) => setSelectedDate(e.target.value)}
      />

      <select
        value={selectedTime}
        onChange={(e) => setSelectedTime(e.target.value)}
      >
        <option value="">Select Time</option>
        {timeSlots.map((time) => (
          <option key={time.id} value={time.id}>
            {time.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default ScheduleControls;
