import React, { useState } from "react";
import "./style.css";
import InputField from "./InputField";

const LoginPage: React.FC = () => {
    const [userId, setUserId] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleLogin = async () => {
        try {
            // TODO: 在这里调用后端API进行身份验证
            console.log("Logging in with", { userId, password });
            // 示例: const response = await fetch("/api/login", { method: "POST", body: JSON.stringify({ userId, password }) });
            // 处理后端返回结果
        } catch (err) {
            setError("登录失败，请检查您的ID和密码。");
        }
    };

    return (
        <div className="container">
            <div className="login-box">
                <h2 className="title">用户登录</h2>
                {error && <p className="error-message">{error}</p>}
                <InputField label="用户ID" type="text" value={userId} onChange={setUserId} />
                <InputField label="密码" type="password" value={password} onChange={setPassword} />
                <button onClick={handleLogin} className="login-button">
                    登录
                </button>
            </div>
        </div>
    );
};

export default LoginPage;
