"use client";

import { useState } from "react";
import { ChevronDown, Heart, Info, Play } from "lucide-react";
import "../styles/cinemas_page.css";

export default function MovieBookingInterface() {
  const [selectedDate, setSelectedDate] = useState("11 APR");
  const [selectedLocation, setSelectedLocation] = useState("1 UTAMA");

  const dates = [
    { day: "TODAY", date: "11 APR", fullDate: "11 Apr 2025" },
    { day: "SAT", date: "12 APR", fullDate: "12 Apr 2025" },
    { day: "SUN", date: "13 APR", fullDate: "13 Apr 2025" },
    { day: "MON", date: "14 APR", fullDate: "14 Apr 2025" },
    { day: "TUE", date: "15 APR", fullDate: "15 Apr 2025" },
    { day: "WED", date: "16 APR", fullDate: "16 Apr 2025" },
    { day: "THU", date: "17 APR", fullDate: "17 Apr 2025" },
    { day: "FRI", date: "18 APR", fullDate: "18 Apr 2025" },
    { day: "SAT", date: "19 APR", fullDate: "19 Apr 2025" },
    { day: "SUN", date: "20 APR", fullDate: "20 Apr 2025" },
    { day: "SAT", date: "26 APR", fullDate: "26 Apr 2025" },
    { day: "SUN", date: "27 APR", fullDate: "27 Apr 2025" },
  ];

  const locations = [
    "1 UTAMA",
    "1 SHAMELIN",
    "AMPANG POINT",
    "AU2 SETIAWANGSA",
    "BUKIT RAJA",
    "BUKIT TINGGI",
    "CENTRAL I-CITY",
    "CHERAS SELATAN",
    "CHERAS SENTRAL",
    "DPULZE CYBERJAYA",
    "PAVILION BUKIT JALIL",
    "STRAND KOTA DAMANSARA",
    "JAYA SEKSYEN 14",
    "KEPONG",
    "MINES",
    "RAWANG",
    "SETIAWALK PUCHONG",
    "SUNWAY PUTRA",
  ];

  const movies = [
    {
      id: 1,
      title: "BLOOD BROTHERS: BARA NAGA",
      image: "/placeholder.svg",
      genres: ["Action", "Drama"],
      duration: "2 hr 9 mins",
      language: "Bahasa Melayu",
      rating: "18",
      cinemas: ["1 UTAMA", "BUKIT RAJA"],
      showtimes: {
        INDULGE: [{ time: "12:15 AM", date: "12 Apr 2025" }],
        BEANIE: [
          { time: "8:45 PM", date: "11 Apr 2025" },
          { time: "11:30 PM", date: "11 Apr 2025" },
        ],
        DELUXE: [
          { time: "8:45 PM", date: "11 Apr 2025" },
          { time: "9:30 PM", date: "11 Apr 2025" },
          { time: "10:45 PM", date: "11 Apr 2025" },
          { time: "11:30 PM", date: "11 Apr 2025" },
          { time: "12:15 AM", date: "12 Apr 2025" },
        ],
      },
    },
    {
      id: 2,
      title: "GOOD BAD UGLY (TAMIL)",
      image: "/placeholder.svg",
      genres: ["Action", "Drama"],
      duration: "2 hr 20 mins",
      language: "Tamil",
      rating: "18",
      cinemas: ["1 UTAMA", "DPULZE CYBERJAYA"],
      showtimes: {
        INDULGE: [
          { time: "9:00 PM", date: "11 Apr 2025" },
          { time: "11:00 PM", date: "11 Apr 2025" },
        ],
        DELUXE: [{ time: "11:00 PM", date: "11 Apr 2025" }],
      },
    },
  ];

  // ✅ Filter movies by location and selectedDate
  const filteredMovies = movies.filter((movie) => {
    const showtimesForDate = Object.values(movie.showtimes)
      .flat()
      .some(
        (session) =>
          session.date === dates.find((d) => d.date === selectedDate)?.fullDate
      );
    return movie.cinemas.includes(selectedLocation) && showtimesForDate;
  });

  return (
    <div className="container">
      {/* Date Selector */}
      <div className="date-selector-container">
        <div className="date-selector">
          {dates.map((date) => (
            <button
              key={date.date}
              onClick={() => setSelectedDate(date.date)}
              className={`date-button ${
                selectedDate === date.date ? "date-button-selected" : ""
              }`}
            >
              <span className="date-day">{date.day}</span>
              <span className="date-number">{date.date}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Location Header */}
      <div className="location-header">
        <div className="location-title">
          <span className="location-name">
            KLANG VALLEY - {selectedLocation}
          </span>
          <div className="info-icon">i</div>
        </div>
        <div className="status-indicators">
          <div className="status-item">
            <div className="status-dot status-available"></div>
            <span className="status-text status-available-text">Available</span>
          </div>
          <div className="status-item">
            <div className="status-dot status-selling"></div>
            <span className="status-text status-selling-text">
              Selling fast
            </span>
          </div>
          <div className="status-item">
            <div className="status-dot status-sold"></div>
            <span className="status-text status-sold-text">Sold out</span>
          </div>
          <div className="filter-button">
            <span className="filter-text">FILTER BY</span>
          </div>
        </div>
      </div>

      <div className="content-grid">
        {/* Location List */}
        <div className="locations-panel">
          <div className="location-header-dropdown">
            <span className="location-region">KLANG VALLEY</span>
            <ChevronDown className="dropdown-icon" />
          </div>
          {locations.map((location) => (
            <div
              key={location}
              className={`location-item ${
                selectedLocation === location ? "location-item-selected" : ""
              }`}
              onClick={() => setSelectedLocation(location)}
            >
              <Heart
                className={`location-heart ${
                  selectedLocation === location ? "location-heart-selected" : ""
                }`}
              />
              <span>{location}</span>
            </div>
          ))}
        </div>

        {/* Movie List */}
        <div className="movies-panel">
          {filteredMovies.length === 0 ? (
            <p>No movies available for this location and date.</p>
          ) : (
            filteredMovies.map((movie) => (
              <div key={movie.id} className="movie-item">
                <div className="movie-content">
                  <div className="movie-poster-container">
                    <img
                      src={movie.image}
                      alt={movie.title}
                      className="poster-image"
                    />
                    <div className="movie-rating">{movie.rating}</div>
                  </div>

                  <div className="movie-details">
                    <div className="movie-header">
                      <h2 className="movie-title">{movie.title}</h2>
                      <div className="movie-meta">
                        <span>{movie.genres.join(", ")}</span>
                        <span className="meta-separator">|</span>
                        <span>{movie.duration}</span>
                        <span className="meta-separator">|</span>
                        <span>{movie.language}</span>
                      </div>
                    </div>

                    <div className="movie-actions">
                      <button className="action-button">
                        <Info className="button-icon" />
                        <span>MORE INFO</span>
                      </button>
                      <button className="action-button">
                        <Play className="button-icon" />
                        <span>WATCH TRAILER</span>
                      </button>
                    </div>

                    {/* Showtimes */}
                    {Object.entries(movie.showtimes).map(([type, sessions]) => {
                      const filteredSessions = sessions.filter(
                        (s) =>
                          s.date ===
                          dates.find((d) => d.date === selectedDate)?.fullDate
                      );
                      if (filteredSessions.length === 0) return null;

                      return (
                        <div key={type} className="showtime-section">
                          <div className="showtime-header">
                            <h3
                              className={`showtime-type showtime-${type.toLowerCase()}`}
                            >
                              {type}
                            </h3>
                            <div className="info-icon">i</div>
                          </div>
                          <div className="showtime-grid">
                            {filteredSessions.map((s, index) => (
                              <button key={index} className="time-button">
                                <div className="time-value">{s.time}</div>
                                <div className="time-date">{s.date}</div>
                              </button>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}
