"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import Navbar from "../../components/Navbar"
import Footer from "../../components/Footer"
import { getMovieById, getScreeningsByMovieId } from "../../services/movieService"
import type { MovieProps } from "../../interfaces/movies"
import type { ScreeningProps } from "../../interfaces/screenings"
import "../styles/movie_details.css"

const MovieDetailsPage: React.FC = () => {
  const { movieId } = useParams<{ movieId: string }>()
  const navigate = useNavigate()
  const [movie, setMovie] = useState<MovieProps | null>(null)
  const [screenings, setScreenings] = useState<ScreeningProps[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [selectedDate, setSelectedDate] = useState<string>("")
  const [selectedCinema, setSelectedCinema] = useState<string>("")
  const [animateContent, setAnimateContent] = useState(false)
  const [activeTab, setActiveTab] = useState("about")

  useEffect(() => {
    const fetchMovieDetails = async () => {
      if (!movieId) return

      try {
        setLoading(true)
        const movieResult = await getMovieById(movieId)
        const screeningsResult = await getScreeningsByMovieId(movieId)

        if (movieResult.success && movieResult.data) {
          setMovie(movieResult.data)
        } else {
          setError(movieResult.message || "Failed to load movie details")
        }

        if (screeningsResult.success && screeningsResult.data) {
          setScreenings(screeningsResult.data)

          // Set default selected date to the first available date
          if (screeningsResult.data.length > 0) {
            const dates = [...new Set(screeningsResult.data.map((s) => s.date))]
            if (dates.length > 0) {
              setSelectedDate(dates[0])
            }
          }
        }
      } catch (err) {
        setError("An error occurred while fetching movie details")
        console.error(err)
      } finally {
        setLoading(false)
        // Trigger animation after data is loaded
        setTimeout(() => {
          setAnimateContent(true)
        }, 100)
      }
    }

    fetchMovieDetails()
  }, [movieId])

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loader"></div>
      </div>
    )
  }

  if (error || !movie) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error || "Movie not found"}</p>
        <button onClick={() => navigate("/movies")} className="back-button">
          Back to Movies
        </button>
      </div>
    )
  }

  // Get unique dates from screenings
  const availableDates = [...new Set(screenings.map((s) => s.date))].sort()

  // Get unique cinemas for the selected date
  const availableCinemas = [...new Set(screenings.filter((s) => s.date === selectedDate).map((s) => s.cinemaId))]

  // Filter screenings by selected date and cinema
  const filteredScreenings = screenings.filter((s) => {
    if (selectedDate && s.date !== selectedDate) return false
    if (selectedCinema && s.cinemaId !== selectedCinema) return false
    return true
  })

  // Group screenings by cinema
  const screeningsByVenue = filteredScreenings.reduce(
    (acc, screening) => {
      if (!acc[screening.cinemaId]) {
        acc[screening.cinemaId] = []
      }
      acc[screening.cinemaId].push(screening)
      return acc
    },
    {} as Record<string, ScreeningProps[]>,
  )

  const handleBooking = (screeningId: string) => {
    navigate(`/booking/${screeningId}`)
  }

  const formatDate = (dateString: string) => {
    const date = new Date(dateString)
    return date.toLocaleDateString("en-US", { weekday: "short", month: "short", day: "numeric" })
  }

  const getCinemaName = (cinemaId: string) => {
    // This would ideally come from a cinema service
    const cinemaMap: Record<string, string> = {
      pavilion: "Pavilion KL",
      boulevard_pvl: "Boulevard Bintulu",
      "1utama": "1 Utama",
    }
    return cinemaMap[cinemaId] || cinemaId
  }

  return (
    <div className="movie-details-page">
      <Navbar />

      <div className="movie-hero" style={{ backgroundImage: `url(${movie.banner || movie.image})` }}>
        <div className="hero-overlay">
          <div className={`movie-info-container ${animateContent ? "animate" : ""}`}>
            <div className="movie-poster">
              <img src={movie.image || "/placeholder.svg"} alt={movie.title} />
              {movie.category === "NOW SHOWING" && <div className="movie-badge now-showing">Now Showing</div>}
              {movie.category === "COMING SOON" && <div className="movie-badge coming-soon">Coming Soon</div>}
              {movie.category === "BOOK EARLY" && <div className="movie-badge book-early">Book Early</div>}
            </div>
            <div className="movie-info">
              <h1>{movie.title}</h1>
              <div className="movie-meta">
                <span className="meta-item">{movie.genre}</span>
                <span className="meta-divider">•</span>
                <span className="meta-item">{movie.duration}</span>
                <span className="meta-divider">•</span>
                <span className="meta-item">
                  {typeof movie.languages === "string" ? movie.languages : movie.languages?.join(", ")}
                </span>
              </div>
              <div className="movie-rating">
                <div className="rating-score">
                  <span className="score">{movie.rating || "8.5"}</span>
                  <span className="max">/10</span>
                </div>
                <div className="rating-stars">
                  {"★".repeat(Math.floor(movie.rating || 8.5))}
                  {"☆".repeat(10 - Math.floor(movie.rating || 8.5))}
                </div>
              </div>
              {movie.hasBookNow && (
                <button
                  className="book-now-cta"
                  onClick={() => {
                    const firstScreening = screenings[0]
                    if (firstScreening) {
                      handleBooking(firstScreening.screeningId)
                    }
                  }}
                >
                  Book Tickets
                </button>
              )}
              {!movie.hasBookNow && movie.category === "COMING SOON" && (
                <button className="notify-button">
                  <span className="notify-icon">🔔</span>
                  Notify Me
                </button>
              )}
            </div>
          </div>
        </div>
      </div>

      <div className="movie-content-container">
        <div className={`movie-tabs ${animateContent ? "animate" : ""}`}>
          <button
            className={`tab-button ${activeTab === "about" ? "active" : ""}`}
            onClick={() => setActiveTab("about")}
          >
            About
          </button>
          <button className={`tab-button ${activeTab === "cast" ? "active" : ""}`} onClick={() => setActiveTab("cast")}>
            Cast & Crew
          </button>
          <button
            className={`tab-button ${activeTab === "reviews" ? "active" : ""}`}
            onClick={() => setActiveTab("reviews")}
          >
            Reviews
          </button>
          {movie.hasBookNow && (
            <button
              className={`tab-button ${activeTab === "showtimes" ? "active" : ""}`}
              onClick={() => setActiveTab("showtimes")}
            >
              Showtimes
            </button>
          )}
        </div>

        <div className={`movie-content ${animateContent ? "animate" : ""}`}>
          {activeTab === "about" && (
            <div className="movie-about">
              <h2>Synopsis</h2>
              <p>{movie.description || "No description available for this movie."}</p>

              <div className="movie-details-grid">
                <div className="detail-item">
                  <h3>Director</h3>
                  <p>{movie.director || "Unknown"}</p>
                </div>
                <div className="detail-item">
                  <h3>Release Date</h3>
                  <p>{movie.releaseDate || "Coming Soon"}</p>
                </div>
                <div className="detail-item">
                  <h3>Genre</h3>
                  <p>{movie.genre}</p>
                </div>
                <div className="detail-item">
                  <h3>Duration</h3>
                  <p>{movie.duration}</p>
                </div>
              </div>

              {movie.trailer && (
                <div className="movie-trailer">
                  <h2>Trailer</h2>
                  <div className="trailer-container">
                    <iframe
                      src={movie.trailer}
                      title={`${movie.title} Trailer`}
                      frameBorder="0"
                      allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                      allowFullScreen
                    ></iframe>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === "cast" && (
            <div className="movie-cast">
              <h2>Cast & Crew</h2>

              {movie.cast && movie.cast.length > 0 ? (
                <div className="cast-grid">
                  {movie.cast.map((actor, index) => (
                    <div className="cast-card" key={index}>
                      <div className="cast-image">
                        <img src={actor.image || `/placeholder.svg?height=150&width=150`} alt={actor.name} />
                      </div>
                      <div className="cast-info">
                        <h3>{actor.name}</h3>
                        <p>{actor.character}</p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="no-data-message">
                  <p>Cast information not available for this movie.</p>
                </div>
              )}

              <h2>Crew</h2>
              <div className="crew-list">
                <div className="crew-item">
                  <h3>Director</h3>
                  <p>{movie.director || "Unknown"}</p>
                </div>
                <div className="crew-item">
                  <h3>Writers</h3>
                  <p>{movie.writers || "Unknown"}</p>
                </div>
                <div className="crew-item">
                  <h3>Producers</h3>
                  <p>{movie.producers || "Unknown"}</p>
                </div>
              </div>
            </div>
          )}

          {activeTab === "reviews" && (
            <div className="movie-reviews">
              <h2>Reviews</h2>

              <div className="reviews-summary">
                <div className="rating-large">
                  <span className="rating-number">{movie.rating || "8.5"}</span>
                  <div className="rating-stars-large">
                    {"★".repeat(Math.floor(movie.rating || 8.5))}
                    {"☆".repeat(10 - Math.floor(movie.rating || 8.5))}
                  </div>
                  <span className="rating-count">based on 245 reviews</span>
                </div>

                <div className="rating-breakdown">
                  <div className="breakdown-item">
                    <span className="breakdown-label">5 ★</span>
                    <div className="breakdown-bar">
                      <div className="breakdown-fill" style={{ width: "70%" }}></div>
                    </div>
                    <span className="breakdown-percent">70%</span>
                  </div>
                  <div className="breakdown-item">
                    <span className="breakdown-label">4 ★</span>
                    <div className="breakdown-bar">
                      <div className="breakdown-fill" style={{ width: "20%" }}></div>
                    </div>
                    <span className="breakdown-percent">20%</span>
                  </div>
                  <div className="breakdown-item">
                    <span className="breakdown-label">3 ★</span>
                    <div className="breakdown-bar">
                      <div className="breakdown-fill" style={{ width: "5%" }}></div>
                    </div>
                    <span className="breakdown-percent">5%</span>
                  </div>
                  <div className="breakdown-item">
                    <span className="breakdown-label">2 ★</span>
                    <div className="breakdown-bar">
                      <div className="breakdown-fill" style={{ width: "3%" }}></div>
                    </div>
                    <span className="breakdown-percent">3%</span>
                  </div>
                  <div className="breakdown-item">
                    <span className="breakdown-label">1 ★</span>
                    <div className="breakdown-bar">
                      <div className="breakdown-fill" style={{ width: "2%" }}></div>
                    </div>
                    <span className="breakdown-percent">2%</span>
                  </div>
                </div>
              </div>

              <div className="user-reviews">
                <div className="review-card">
                  <div className="reviewer-info">
                    <div className="reviewer-avatar">JD</div>
                    <div className="reviewer-name">John Doe</div>
                    <div className="review-date">2 days ago</div>
                  </div>
                  <div className="review-rating">★★★★★</div>
                  <h3 className="review-title">Absolutely amazing!</h3>
                  <p className="review-content">
                    This movie exceeded all my expectations. The plot was engaging from start to finish, and the
                    performances were outstanding. Highly recommend watching it in theaters for the full experience.
                  </p>
                </div>

                <div className="review-card">
                  <div className="reviewer-info">
                    <div className="reviewer-avatar">JS</div>
                    <div className="reviewer-name">Jane Smith</div>
                    <div className="review-date">1 week ago</div>
                  </div>
                  <div className="review-rating">★★★★☆</div>
                  <h3 className="review-title">Great movie with minor flaws</h3>
                  <p className="review-content">
                    I really enjoyed this film overall. The cinematography was breathtaking and the acting was solid. My
                    only criticism would be that the middle section dragged a bit, but the ending more than made up for
                    it.
                  </p>
                </div>

                <div className="review-card">
                  <div className="reviewer-info">
                    <div className="reviewer-avatar">RJ</div>
                    <div className="reviewer-name">Robert Johnson</div>
                    <div className="review-date">2 weeks ago</div>
                  </div>
                  <div className="review-rating">★★★★★</div>
                  <h3 className="review-title">A masterpiece!</h3>
                  <p className="review-content">
                    One of the best films I've seen this year. The director's vision really shines through in every
                    scene. The soundtrack perfectly complements the storytelling. I'll definitely be watching this
                    again.
                  </p>
                </div>

                <button className="load-more-button">Load More Reviews</button>
              </div>
            </div>
          )}

          {activeTab === "showtimes" && movie.hasBookNow && (
            <div className="movie-showtimes">
              <h2>Showtimes</h2>

              <div className="date-selector">
                {availableDates.map((date) => (
                  <button
                    key={date}
                    className={`date-button ${selectedDate === date ? "active" : ""}`}
                    onClick={() => {
                      setSelectedDate(date)
                      setSelectedCinema("")
                    }}
                  >
                    {formatDate(date)}
                  </button>
                ))}
              </div>

              {selectedDate && (
                <div className="cinema-selector">
                  <button
                    className={`cinema-button ${selectedCinema === "" ? "active" : ""}`}
                    onClick={() => setSelectedCinema("")}
                  >
                    All Cinemas
                  </button>
                  {availableCinemas.map((cinema) => (
                    <button
                      key={cinema}
                      className={`cinema-button ${selectedCinema === cinema ? "active" : ""}`}
                      onClick={() => setSelectedCinema(cinema)}
                    >
                      {getCinemaName(cinema)}
                    </button>
                  ))}
                </div>
              )}

              {Object.keys(screeningsByVenue).length > 0 ? (
                <div className="screenings-list">
                  {Object.entries(screeningsByVenue).map(([cinemaId, venueScreenings]) => (
                    <div className="cinema-screenings" key={cinemaId}>
                      <h3 className="cinema-name">{getCinemaName(cinemaId)}</h3>
                      <div className="screening-times">
                        {venueScreenings.map((screening) => (
                          <button
                            key={screening.screeningId}
                            className="time-button"
                            onClick={() => handleBooking(screening.screeningId)}
                          >
                            {screening.time}
                          </button>
                        ))}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="no-screenings">
                  <p>No screenings available for the selected date.</p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>

      <div className={`similar-movies ${animateContent ? "animate" : ""}`}>
        <h2>You May Also Like</h2>
        <div className="similar-movies-grid">
          {/* This would ideally be populated with actual similar movies */}
          {[1, 2, 3, 4].map((index) => (
            <div className="similar-movie-card" key={index} onClick={() => navigate(`/movie/${index}`)}>
              <div className="similar-movie-poster">
                <img src={`/placeholder.svg?height=300&width=200`} alt="Similar movie" />
              </div>
              <h3>Similar Movie {index}</h3>
              <p>Action, Adventure • 2h 15m</p>
            </div>
          ))}
        </div>
      </div>

      <Footer />
    </div>
  )
}

export default MovieDetailsPage
