import React, { useState } from "react";
import "../styles/AddMovie.css";
import dummyData from "../config/dummydata.json";

const AddMovie = () => {
  const [poster, setPoster] = useState<string | null>(null);
  const [movieName, setMovieName] = useState("");
  const [releaseDate, setReleaseDate] = useState("");
  const [duration, setDuration] = useState("");
  const [movieType, setMovieType] = useState("");
  const [selectedSchedules, setSelectedSchedules] = useState<string[]>([]);
  const [selectedLocations, setSelectedLocations] = useState<string[]>([]);
  const [ticketPriceAdult, setTicketPriceAdult] = useState("0.00");
  const [ticketPriceChild, setTicketPriceChild] = useState("0.00");
  const [ticketPriceOKU, setTicketPriceOKU] = useState("0.00");

  const handleImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        // Set the image to state and trigger re-render
        setPoster(reader.result as string);
      };
      reader.readAsDataURL(file); // Load the file as data URL
    }
  };

  const toggleLocation = (location: string) => {
    setSelectedLocations((prev) =>
      prev.includes(location) ? prev.filter((loc) => loc !== location) : [...prev, location]
    );
  };

  return (
    <div className="add-movie-page">
      <h2>Add Movie Details</h2>
      <div className="movie-details">
        {/* Movie Poster Preview Box */}
        <div className="movie-poster">
          <div className="poster-box">
            {poster ? (
              <img src={poster} alt="Movie Poster" className="poster-preview" />
            ) : (
              <p>No Image</p> // Show a placeholder text when no image is selected
            )}
          </div>
          {/* File Input for Uploading */}
          <input
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            key={poster ? "uploaded" : "default"} // Forces re-render when the poster is updated
          />
        </div>

        {/* Movie Form */}
        <div className="movie-form">
          <label>Movie Name:</label>
          <input type="text" value={movieName} onChange={(e) => setMovieName(e.target.value)} />

          <label>Release Date:</label>
          <input type="date" value={releaseDate} onChange={(e) => setReleaseDate(e.target.value)} />

          <label>End Date:</label>
          <input type="date" />

          <label htmlFor="durations">Durations:</label>
          <select id="durations" name="durations" value={duration} onChange={(e) => setDuration(e.target.value)}>
            {dummyData.durations.map((d) => (
              <option key={d.value} value={d.value}>
                {d.label}
              </option>
            ))}
          </select>

          <label>Movie Type:</label>
          <select value={movieType} onChange={(e) => setMovieType(e.target.value)}>
            <option value="">Select a movie type</option>
            {dummyData.movieTypes.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>

          <label>Locations:</label>
          <div className="locations">
            {dummyData.locations.map((loc) => (
              <button
                key={loc}
                className={selectedLocations.includes(loc) ? "selected" : ""}
                onClick={() => toggleLocation(loc)}
              >
                {loc}
              </button>
            ))}
          </div>

          <label>Ticket Price:</label>
          <div className="ticket-prices">
            <div>
              <label>Adult:</label>
              <input type="text" value={ticketPriceAdult} onChange={(e) => setTicketPriceAdult(e.target.value)} />
            </div>
            <div>
              <label>Child:</label>
              <input type="text" value={ticketPriceChild} onChange={(e) => setTicketPriceChild(e.target.value)} />
            </div>
            <div>
              <label>OKU:</label>
              <input type="text" value={ticketPriceOKU} onChange={(e) => setTicketPriceOKU(e.target.value)} />
            </div>
          </div>

          <br />

          <div className="buttons">
            <button>Update Movie</button>
            <button>Delete Movie</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AddMovie;
