import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../../styles/navbar.css";

const Navbar: React.FC = () => {
  const navigate = useNavigate();
  const [showCinemaDropdown, setShowCinemaDropdown] = useState(false);

  const cinemas = [
    { id: "1utama", name: "1 UTAMA" },
    { id: "pavilion", name: "PAVILION BUKIT JALIL" },
  ];

  const navigateToCinema = (id: string) => {
    navigate(`/showtimes/${id}`);
  };

  return (
    <header className="header">
      <div className="header-left">
        <a href="/" className="logo">
          <img src="/assets/logo.png" alt="Logo" className="logo-img" />
        </a>

        <div className="dropdown">
          <button
            className="location-button"
            onClick={() => setShowCinemaDropdown(!showCinemaDropdown)}
          >
            üìç ALL STATES/AREAS
          </button>
          {showCinemaDropdown && (
            <div className="dropdown-content">
              <h3>Select a Cinema</h3>
              <div className="cinema-dropdown-list">
                {cinemas.map((cinema) => (
                  <div
                    key={cinema.id}
                    className="cinema-dropdown-item"
                    onClick={() => navigateToCinema(cinema.id)}
                  >
                    {cinema.name}
                  </div>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      <nav className="main-nav">
        <a href="/movies" className="nav-link">
          MOVIES
        </a>
        <a href="/cinemas" className="nav-link">
          CINEMAS
        </a>
        <a href="/food-drinks" className="nav-link">
          FOOD & DRINKS
        </a>
        <a href="/promotions" className="nav-link">
          PROMOTIONS
        </a>
        <a href="/login" className="sign-in-button">
          üë§ SIGN IN
        </a>
      </nav>
    </header>
  );
};

export default Navbar;
