import React, { useState } from "react";
import "./MovieBooking.css";

const MovieBooking: React.FC = () => {
  const [selectedTime, setSelectedTime] = useState("6:00 PM");
  const [selectedSeats, setSelectedSeats] = useState<string[]>([]);

  const showtimes = [
    { time: "11:45 AM", date: "12 Apr 2025" },
    { time: "2:00 PM", date: "12 Apr 2025" },
    { time: "6:00 PM", date: "12 Apr 2025" },
    { time: "8:00 PM", date: "12 Apr 2025" },
    { time: "12:10 AM", date: "13 Apr 2025" },
  ];

  // Generate theater layout
  const rows = ["A", "B", "C", "D", "E"];
  const seatsPerRow = 10;

  // Predefined sold seats
  const soldSeats = ["E1", "E2"];

  const handleSeatClick = (seat: string) => {
    if (soldSeats.includes(seat)) return;

    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getSeatStatus = (seat: string) => {
    if (selectedSeats.includes(seat)) return "selected";
    if (soldSeats.includes(seat)) return "sold";
    return "available";
  };

  return (
    <div className="main-content">
      <div className="container">
        {/* Header */}
        <div className="header">
          <button className="back-button">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="m12 19-7-7 7-7" />
              <path d="M19 12H5" />
            </svg>
          </button>
          <div className="progress-indicator">
            <div className="progress-step active">1</div>
            <div className="progress-line"></div>
            <div className="progress-step inactive">2</div>
          </div>
          <button className="close-button">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <path d="M18 6 6 18" />
              <path d="m6 6 12 12" />
            </svg>
          </button>
        </div>

        <div className="booking-body">
          {/* Movie Info */}
          <div className="movie-info">
            <h1 className="movie-title">BLOOD BROTHERS: BARA NAGA</h1>
            <div className="movie-meta">
              <span>Action, Drama</span>
              <span>|</span>
              <span>2 hr 9 mins</span>
              <span>|</span>
              <span>Bahasa Melayu</span>
            </div>

            <div className="movie-details">
              <div className="detail-tag">1 UTAMA</div>
              <div className="detail-tag">INDULGE 1</div>
              <div className="detail-tag">12 Apr 2025, 6:00 PM</div>
            </div>

            <h3 className="showtimes-label">Available showtimes</h3>
            <div className="showtimes-container">
              {showtimes.map((show) => (
                <button
                  key={show.time}
                  onClick={() => setSelectedTime(show.time)}
                  className={`showtime-button ${
                    selectedTime === show.time ? "active" : ""
                  }`}
                >
                  <div>{show.time}</div>
                  {show.date !== "12 Apr 2025" && (
                    <div className="showtime-date">{show.date}</div>
                  )}
                </button>
              ))}
            </div>
          </div>

          {/* Seat Legend */}
          <div className="legend">
            <div className="legend-item">
              <div className="legend-indicator selected"></div>
              <span>SELECTED</span>
            </div>
            <div className="legend-item">
              <div className="legend-indicator sold"></div>
              <span>SOLD</span>
            </div>
            <div className="legend-item">
              <div className="legend-indicator single"></div>
              <span>SINGLE</span>
            </div>
            <div className="legend-item">
              <div className="legend-indicator twin">
                <div className="twin-divider"></div>
              </div>
              <span>TWIN</span>
            </div>
          </div>

          {/* Screen */}
          <div className="screen-container">
            <div className="screen"></div>
            <div className="screen-label">SCREEN</div>
          </div>

          {/* Seats */}
          <div className="seats-container">
            <div className="theater-layout">
              {/* Left row labels */}
              <div className="row-labels">
                {rows.map((row) => (
                  <div key={`left-${row}`} className="row-label">
                    {row}
                  </div>
                ))}
              </div>

              {/* Seats grid */}
              <div className="seats-grid">
                {rows.flatMap((row) =>
                  Array.from({ length: seatsPerRow }, (_, i) => {
                    const seatId = `${row}${i + 1}`;
                    const status = getSeatStatus(seatId);

                    return (
                      <button
                        key={seatId}
                        onClick={() => handleSeatClick(seatId)}
                        disabled={status === "sold"}
                        className={`seat ${status}`}
                      >
                        <div className="seat-inner">
                          {i % 2 === 0 && <div className="seat-divider"></div>}
                        </div>
                      </button>
                    );
                  })
                )}
              </div>

              {/* Right row labels */}
              <div className="row-labels">
                {rows.map((row) => (
                  <div key={`right-${row}`} className="row-label">
                    {row}
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Book button */}
          <div className="book-button-container">
            <button
              className="book-button"
              disabled={selectedSeats.length === 0}
            >
              BOOK SEATS!
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MovieBooking;
