"use client";
import { useParams, useNavigate } from "react-router-dom";
import { mockMovies } from "../data/mockData";
import { ChevronDown, ChevronLeft } from "lucide-react";
import "../styles/moviedetails.css";
import Navbar from "../components/Navbar";
import Footer from "../components/Footer";
import { useEffect, useState } from "react";
import { useMemo } from "react";

const generateDateRange = (days: number) => {
  const dates: string[] = [];
  const today = new Date();

  for (let i = 0; i < days; i++) {
    const future = new Date(today);
    future.setDate(today.getDate() + i);
    dates.push(future.toISOString().split("T")[0]); // e.g. "2025-04-08"
  }

  return dates;
};

const MovieDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const movie = mockMovies.find((m) => m.id === Number(id));
  const [selectedDate, setSelectedDate] = useState<string>("");

  const uniqueDates = useMemo(() => {
    if (!movie) return [];

    // Flatten all dates from nested sessions
    const allDates = movie.screenings.flatMap((screening) =>
      screening.sessions.map((session) => session.date)
    );

    const unique = Array.from(new Set(allDates));
    unique.sort((a, b) => new Date(a).getTime() - new Date(b).getTime());
    return unique;
  }, [movie]);

  useEffect(() => {
    // Default to today's date or first screening date available
    const today = new Date().toISOString().split("T")[0];
    const availableDates = movie?.screenings.flatMap((s) =>
      s.sessions.map((s) => s.date)
    );
    if (availableDates?.includes(today)) {
      setSelectedDate(today);
    } else {
      setSelectedDate(availableDates?.[0] || "");
    }
  }, [movie]);

  if (!movie) {
    return (
      <>
        <Navbar />
        <div className="not-found">Movie not found.</div>
        <Footer />
      </>
    );
  }

  const dateRange = generateDateRange(12); // today + 11 days
  const sessionDates = new Set(
    movie.screenings.flatMap((s) => s.sessions.map((s) => s.date))
  );

  const filteredScreenings = movie.screenings
    .map((cinema) => ({
      ...cinema,
      sessions: cinema.sessions.filter((s) => s.date === selectedDate),
    }))
    .filter((c) => c.sessions.length > 0);

  return (
    <>
      <Navbar />
      <div className="movie-details-container">
        {/* Banner */}
        <div
          className="banner"
          style={{ backgroundImage: `url(${movie.banner})` }}
        >
          <button className="back-button" onClick={() => navigate(-1)}>
            <ChevronLeft size={20} />
            Back
          </button>
          <div className="details-overlay">
            <h1 className="title">{movie.title}</h1>
            <p className="metadata">
              {movie.genre} | {movie.duration} | {movie.languages.join(", ")}
            </p>
            <div className="button-group">
              <button className="info-btn">
                More Info <ChevronDown size={16} />
              </button>
              <button className="trailer-btn">Watch Trailer</button>
            </div>
          </div>
        </div>

        {/* Date Tab Bar */}
        <div className="date-bar">
          {uniqueDates.map((dateStr, i) => {
            const date = new Date(dateStr);
            const isToday = date.toDateString() === new Date().toDateString();

            return (
              <div
                key={dateStr}
                className={`date-tab ${
                  selectedDate === dateStr ? "active" : ""
                }`}
                onClick={() => setSelectedDate(dateStr)}
              >
                {isToday && <span className="label">TODAY</span>}
                <span className="date-text">
                  {date.toLocaleDateString("en", {
                    day: "2-digit",
                    month: "short",
                  })}
                </span>
              </div>
            );
          })}
        </div>

        {/* Booking Section */}
        <div className="booking-section">
          <div className="cinema-info-header">
            <p className="location">Cinemas Showing on {selectedDate}</p>
            <div className="legend">
              <span className="available">✓ Available</span>
              <span className="fast">🟡 Selling fast</span>
              <span className="sold">🔴 Sold out</span>
            </div>
          </div>

          <div className="cinema-list">
            {filteredScreenings.map((screen) => (
              <div key={screen.cinema} className="cinema-block">
                <h4>{screen.cinema}</h4>
                <div className="times">
                  {screen.sessions.map((session) => (
                    <button key={session.time} className="time-btn">
                      {session.time}
                    </button>
                  ))}
                </div>
              </div>
            ))}
          </div>

          <button className="cinema-details-toggle">
            <span>🎬 Cinema Details</span>
            <ChevronDown size={16} />
          </button>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default MovieDetails;
