import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Import Axios
import { ChevronRight } from "lucide-react";
import "../../styles/landing_page.css"; // Your styles

import { MovieProps } from "../../interfaces/movies";
import { HeroSlideProps } from "../../interfaces/heroSlide";
import { CinemaProps } from "../../interfaces/cinemas";

const CinemaWebsite: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<string>("NOW SHOWING");
  const [currentSlide, setCurrentSlide] = useState<number>(0);
  const [showCinemaDropdown, setShowCinemaDropdown] = useState<boolean>(false);
  const [movies, setMovies] = useState<MovieProps[]>([]);
  const [cinemas, setCinemas] = useState<CinemaProps[]>([]);
  const [heroSlides, setHeroSlides] = useState<HeroSlideProps[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const tabs: string[] = ["NOW SHOWING", "BOOK EARLY", "COMING SOON"];

  useEffect(() => {
    setLoading(true);
    // Simulating empty data fields for testing
    setHeroSlides([]);
    setMovies([]);
    setCinemas([]);
    setLoading(false);
  }, []);

  const navigateToCinema = (cinemaId: string, movieId?: number) => {
    const path = `/showtimes/${cinemaId}${movieId ? `?movie=${movieId}` : ""}`;
    navigate(path);
  };

  const handleButtonClick = (path: string) => {
    navigate(path);
  };

  if (loading) {
    return <div>Loading...</div>; // Show loading text or a spinner
  }

  if (error) {
    return <div>{error}</div>; // Display error message if any API call failed
  }

  return (
    <div className="cinema-container">
      {/* Hero Slideshow */}
      <section className="hero-slideshow">
        {heroSlides.length === 0 ? (
          <div>No Hero Slides Available</div>
        ) : (
          heroSlides.map((slide, index) => (
            <div
              key={slide.id}
              className={`hero-slide ${index === currentSlide ? "active" : ""}`}
              style={{
                backgroundImage: `linear-gradient(rgba(0,0,0,0.5), rgba(0,0,0,0.5)), url(${
                  slide.image || "/placeholder.jpg"
                })`,
              }}
            >
              <div className="hero-content">
                <h1>{slide.title || "No Title Available"}</h1>
                <p>{slide.subtitle || "No Subtitle Available"}</p>
                <button
                  className="hero-button"
                  onClick={() => handleButtonClick(slide.buttonLink)}
                >
                  {slide.buttonText || "Learn More"}
                </button>
              </div>
            </div>
          ))
        )}
      </section>

      {/* Movie Tabs */}
      <div className="movie-tabs-container">
        <div className="movie-tabs-wrapper">
          <div className="movie-tabs">
            {tabs.map((tab) => (
              <button
                key={tab}
                className={`tab-button ${activeTab === tab ? "active" : ""}`}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
              </button>
            ))}
          </div>
          <a href="/all-movies" className="view-all-link">
            VIEW ALL
            <span className="arrow-icon">→</span>
          </a>
        </div>

        {/* Movie Grid */}
        <div className="movie-grid">
          {movies.length === 0 ? (
            <div>No Movies Available</div> // Show this if no movies are found
          ) : (
            movies.map((movie) => (
              <div key={movie.id} className="movie-card">
                {/* Movie Poster */}
                <div className="movie-poster">
                  <img
                    src={movie.image || "/placeholder.svg"}
                    alt={movie.title || "No Image Available"}
                    className="poster-image"
                  />
                  {/* More Movie Details */}
                </div>
                <div className="movie-title">
                  {movie.title || "No Title Available"}
                </div>
              </div>
            ))
          )}

          {/* Carousel Navigation */}
          <button className="carousel-nav-button">
            <span className="carousel-arrow">→</span>
          </button>
        </div>
      </div>

      {/* Cinemas */}
      <div className="cinema-dropdown">
        {cinemas.length === 0 ? (
          <div>No Cinemas Available</div>
        ) : (
          <ul>
            {cinemas.map((cinema) => (
              <li key={cinema.id} onClick={() => navigateToCinema(cinema.id)}>
                {cinema.name || "No Name Available"}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default CinemaWebsite;
