import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Navbar from "../components/NavBar";
import Footer from "../components/Footer";
import "../styles/landing_page.css";

import { MovieProps } from "../interfaces/movies";
import { HeroSlideProps } from "../interfaces/heroSlide";
import { CinemaProps } from "../interfaces/cinemas";

const CinemaWebsite: React.FC = () => {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState<string>("NOW SHOWING");
  const [currentSlide, setCurrentSlide] = useState<number>(0);
  const [movies, setMovies] = useState<MovieProps[]>([]);
  const [cinemas, setCinemas] = useState<CinemaProps[]>([]);
  const [heroSlides, setHeroSlides] = useState<HeroSlideProps[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const tabs: string[] = ["NOW SHOWING", "BOOK EARLY", "COMING SOON", "ALL"];

  useEffect(() => {
    setLoading(true);

    const mockHeroSlides: HeroSlideProps[] = [
      { id: 1, image: "https://imgur.com/mIo5pss.png" },
      { id: 2, image: "https://i.imgur.com/yMmAFJl.png" },
    ];

    const mockMovies: MovieProps[] = [
      {
        id: 1,
        title: "Bad Boys",
        image: "https://imgur.com/estKH6K.png",
        rating: "P12",
        category: "BOOK EARLY",
      },
      {
        id: 2,
        title: "INCEPTION",
        image: "https://imgur.com/LRvKBpv.png",
        rating: "P13",
        category: "NOW SHOWING",
      },
      {
        id: 3,
        title: "JOKER",
        image: "https://imgur.com/KtPNGQg.png",
        rating: "G",
        category: "COMING SOON",
      },
      {
        id: 4,
        title: "ALIEN",
        image: "https://imgur.com/CGyGhxl.png",
        rating: "G",
        category: "NOW SHOWING",
      },
    ];

    const mockCinemas: CinemaProps[] = [
      { id: "1utama", name: "1 UTAMA" },
      { id: "pavilion", name: "PAVILION BUKIT JALIL" },
    ];

    setTimeout(() => {
      setHeroSlides(mockHeroSlides);
      setMovies(mockMovies);
      setCinemas(mockCinemas);
      setLoading(false);
    }, 1000);
  }, []);

  useEffect(() => {
    if (heroSlides.length === 0) return;
    const interval = setInterval(() => {
      setCurrentSlide((prev) =>
        prev === heroSlides.length - 1 ? 0 : prev + 1
      );
    }, 5000);
    return () => clearInterval(interval);
  }, [heroSlides]);

  const navigateToCinema = (cinemaId: string, movieId?: number) => {
    const path = `/showtimes/${cinemaId}${movieId ? `?movie=${movieId}` : ""}`;
    navigate(path);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="cinema-container">
      <Navbar />

      {/* Hero Slideshow */}
      <section className="hero-slideshow">
        {heroSlides.map((slide, index) => (
          <div
            key={slide.id}
            className={`hero-slide ${index === currentSlide ? "active" : ""}`}
            style={{
              backgroundImage: `url(${slide.image || "/placeholder.jpg"})`,
            }}
          />
        ))}
        <div className="hero-fade-bottom" />
      </section>

      {/* Movie Tabs */}
      <div className="movie-tabs-section">
        <div className="tabs-wrapper">
          <div className="movie-tabs">
            {tabs.map((tab) => (
              <button
                key={tab}
                className={`tab-button ${activeTab === tab ? "active" : ""}`}
                onClick={() => {
                  if (tab === "ALL") navigate("/movies");
                  else setActiveTab(tab);
                }}
              >
                {tab}
              </button>
            ))}
          </div>
        </div>

        <div className="movie-grid-wrapper">
          <div className="movie-grid">
            {movies.filter((movie) => movie.category === activeTab).length ===
            0 ? (
              <div>No Movies Available</div>
            ) : (
              movies
                .filter((movie) => movie.category === activeTab)
                .map((movie) => (
                  <Link
                    to={`/movies/${movie.id}`} // Navigates to the detailed page
                    key={movie.id}
                    className="movie-card"
                  >
                    <div className="movie-poster">
                      <img
                        src={movie.image || "/placeholder.svg"}
                        alt={movie.title}
                        className="poster-image"
                      />
                    </div>
                    <div className="movie-title">{movie.title}</div>
                  </Link>
                ))
            )}
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default CinemaWebsite;
