import React, { useState } from "react";
import "../styles/seat_booking.css";
import { mockMovies } from "../../data/mockData";

// Temporary hardcoded selected movie — replace with route-based logic later
const selectedMovie = mockMovies[1]; // THE MARTIAN

const SeatBookingPage: React.FC = () => {
  const [selectedTime, setSelectedTime] = useState("11:00"); // You can default to first session later
  const [selectedSeats, setSelectedSeats] = useState<string[]>([]);

  const rows = ["A", "B", "C", "D", "E"];
  const seatsPerRow = 10;
  const soldSeats = ["E1", "E2"];

  const handleSeatClick = (seat: string) => {
    if (soldSeats.includes(seat)) return;
    setSelectedSeats((prev) =>
      prev.includes(seat) ? prev.filter((s) => s !== seat) : [...prev, seat]
    );
  };

  const getSeatStatus = (seat: string) => {
    if (selectedSeats.includes(seat)) return "selected";
    if (soldSeats.includes(seat)) return "sold";
    return "available";
  };

  return (
    <div className="seat-booking-wrapper">
      {/* 🎬 Banner Section with Gradient Fade */}
      <div
        className="banner-section"
        style={{ backgroundImage: `url(${selectedMovie.banner})` }}
      >
        <div className="banner-overlay">
          <div className="booking-header">
            <div className="header-inner">
              <button className="back-button">←</button>

              <div className="header-center">
                <div className="progress-indicator">
                  <div className="progress-step active">1</div>
                  <div className="progress-line"></div>
                  <div className="progress-step inactive">2</div>
                </div>
              </div>

              <button className="close-button">×</button>
            </div>
          </div>

          {/* 📝 Dynamic Movie Info Section */}
          <section className="movie-info">
            <h1 className="movie-title">{selectedMovie.title}</h1>
            <p className="movie-meta">
              {selectedMovie.genre} | {selectedMovie.duration} |{" "}
              {selectedMovie.languages.join(", ")}
            </p>
            <div className="movie-details">
              <span className="detail-tag">Pavilion KL</span>
              <span className="detail-tag">Room A</span>
              <span className="detail-tag">11 Apr 2025, {selectedTime}</span>
            </div>
            <h3 className="showtimes-label">Available showtimes</h3>
            <div className="showtimes-container">
              {selectedMovie.screenings[0].sessions.map((session) => (
                <button
                  key={session.time}
                  className={`showtime-button ${
                    selectedTime === session.time ? "active" : ""
                  }`}
                  onClick={() => setSelectedTime(session.time)}
                >
                  <div>{session.time}</div>
                  <div className="showtime-date">{session.date}</div>
                </button>
              ))}
            </div>
          </section>
        </div>
      </div>

      {/* 🎟️ Rest of Booking Layout stays same */}
      <main className="booking-body">
        {/* ... legend, screen, seats, book button */}
        {/* Keep as is for now */}
      </main>
    </div>
  );
};

export default SeatBookingPage;
