"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import Navbar from "../../components/Navbar"
import Footer from "../../components/Footer"
import Seat from "../../components/Seat"
import SeatLegend from "../../components/SeatLegend"
import TicketPopup from "../../components/TicketPopup"
import Timeout_Popup from "../../components/Timeout_Popup"
import { getScreeningById } from "../../services/screeningService"
import { getMovieById } from "../../services/movieService"
import { getCinemaById } from "../../services/cinemaService"
import { bookSeats } from "../../services/bookingService"
import type { ScreeningProps } from "../../interfaces/screenings"
import type { MovieProps } from "../../interfaces/movies"
import type { CinemaProps, RoomProps } from "../../interfaces/cinemas"
import type { SeatProps } from "../../interfaces/seats"
import "../styles/seat_booking.css"

const SeatBookingPage: React.FC = () => {
  const { screeningId } = useParams<{ screeningId: string }>()
  const navigate = useNavigate()

  const [screening, setScreening] = useState<ScreeningProps | null>(null)
  const [movie, setMovie] = useState<MovieProps | null>(null)
  const [cinema, setCinema] = useState<CinemaProps | null>(null)
  const [room, setRoom] = useState<RoomProps | null>(null)
  const [selectedSeats, setSelectedSeats] = useState<SeatProps[]>([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)
  const [showTicketPopup, setShowTicketPopup] = useState(false)
  const [showTimeoutPopup, setShowTimeoutPopup] = useState(false)
  const [timeLeft, setTimeLeft] = useState(600) // 10 minutes in seconds
  const [animateContent, setAnimateContent] = useState(false)

  // Seat types and prices (this would ideally come from the API)
  const seatTypes = {
    standard: { name: "Standard", price: 15 },
    premium: { name: "Premium", price: 20 },
    vip: { name: "VIP", price: 25 },
    couple: { name: "Couple", price: 40 },
  }

  useEffect(() => {
    const fetchBookingData = async () => {
      if (!screeningId) return

      try {
        setLoading(true)

        // Fetch screening data
        const screeningResult = await getScreeningById(screeningId)
        if (!screeningResult.success || !screeningResult.data) {
          setError(screeningResult.message || "Failed to load screening details")
          return
        }

        const screeningData = screeningResult.data
        setScreening(screeningData)

        // Fetch movie data
        const movieResult = await getMovieById(screeningData.movieId)
        if (movieResult.success && movieResult.data) {
          setMovie(movieResult.data)
        }

        // Fetch cinema and room data
        const cinemaResult = await getCinemaById(screeningData.cinemaId)
        if (cinemaResult.success && cinemaResult.data) {
          setCinema(cinemaResult.data)

          // Find the room for this screening
          const roomData = cinemaResult.data.rooms.find((r) => r.roomId === screeningData.roomId)
          if (roomData) {
            setRoom(roomData)
          }
        }
      } catch (err) {
        setError("An error occurred while fetching booking details")
        console.error(err)
      } finally {
        setLoading(false)
        // Trigger animation after data is loaded
        setTimeout(() => {
          setAnimateContent(true)
        }, 100)
      }
    }

    fetchBookingData()

    // Start the countdown timer
    const timer = setInterval(() => {
      setTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(timer)
          setShowTimeoutPopup(true)
          return 0
        }
        return prev - 1
      })
    }, 1000)

    return () => clearInterval(timer)
  }, [screeningId])

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60)
    const secs = seconds % 60
    return `${mins}:${secs < 10 ? "0" : ""}${secs}`
  }

  const handleSeatClick = (seat: SeatProps) => {
    // Check if seat is already booked
    if (seat.isBooked) return

    // Check if seat is already selected
    const isSelected = selectedSeats.some((s) => s.seatId === seat.seatId)

    if (isSelected) {
      // Remove seat from selection
      setSelectedSeats(selectedSeats.filter((s) => s.seatId !== seat.seatId))
    } else {
      // Add seat to selection
      setSelectedSeats([...selectedSeats, seat])
    }
  }

  const calculateTotal = () => {
    return selectedSeats.reduce((total, seat) => {
      const seatType = seat.type.toLowerCase() as keyof typeof seatTypes
      return total + (seatTypes[seatType]?.price || seatTypes.standard.price)
    }, 0)
  }

  const handleProceedToCheckout = async () => {
    if (selectedSeats.length === 0) return

    try {
      if (!screening) return

      const result = await bookSeats(
        screening.screeningId,
        selectedSeats.map((seat) => seat.seatId),
      )

      if (result.success) {
        setShowTicketPopup(true)
      } else {
        setError(result.message || "Failed to book seats")
      }
    } catch (err) {
      setError("An error occurred during booking")
      console.error(err)
    }
  }

  const handleCloseTicketPopup = () => {
    setShowTicketPopup(false)
    navigate("/")
  }

  const handleCloseTimeoutPopup = () => {
    setShowTimeoutPopup(false)
    navigate("/movies")
  }

  const getSeatTypePrice = (type: string) => {
    const seatType = type.toLowerCase() as keyof typeof seatTypes
    return seatTypes[seatType]?.price || seatTypes.standard.price
  }

  if (loading) {
    return (
      <div className="loading-screen">
        <div className="loader"></div>
      </div>
    )
  }

  if (error || !screening || !movie || !cinema || !room) {
    return (
      <div className="error-container">
        <h2>Error</h2>
        <p>{error || "Booking information not found"}</p>
        <button onClick={() => navigate("/movies")} className="back-button">
          Back to Movies
        </button>
      </div>
    )
  }

  return (
    <div className="seat-booking-page">
      <Navbar />

      <div className="booking-header">
        <div className="booking-timer">
          <span className="timer-icon">‚è±Ô∏è</span>
          <span className={`timer-text ${timeLeft < 60 ? "expiring" : ""}`}>
            Session expires in: {formatTime(timeLeft)}
          </span>
        </div>
      </div>

      <div className="booking-content">
        <div className={`booking-info-sidebar ${animateContent ? "animate" : ""}`}>
          <div className="movie-summary">
            <div className="movie-poster">
              <img src={movie.image || "/placeholder.svg"} alt={movie.title} />
            </div>
            <div className="movie-details">
              <h1>{movie.title}</h1>
              <div className="movie-meta">
                <span>{movie.genre}</span>
                <span className="meta-divider">‚Ä¢</span>
                <span>{movie.duration}</span>
              </div>
              <div className="screening-details">
                <div className="detail-item">
                  <span className="detail-icon">üìÖ</span>
                  <span>
                    {new Date(screening.date).toLocaleDateString("en-US", {
                      weekday: "long",
                      month: "long",
                      day: "numeric",
                    })}
                  </span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">‚è∞</span>
                  <span>{screening.time}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">üè¢</span>
                  <span>{cinema.name}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-icon">üé¶</span>
                  <span>Hall {room.name}</span>
                </div>
              </div>
            </div>
          </div>

          <div className="booking-summary">
            <h2>Booking Summary</h2>

            {selectedSeats.length > 0 ? (
              <>
                <div className="selected-seats-list">
                  {selectedSeats.map((seat) => (
                    <div className="selected-seat-item" key={seat.seatId}>
                      <div className="seat-info">
                        <span className="seat-label">
                          {seat.row}
                          {seat.number}
                        </span>
                        <span className="seat-type">{seat.type}</span>
                      </div>
                      <span className="seat-price">${getSeatTypePrice(seat.type).toFixed(2)}</span>
                    </div>
                  ))}
                </div>

                <div className="booking-totals">
                  <div className="subtotal-line">
                    <span>Subtotal</span>
                    <span>${calculateTotal().toFixed(2)}</span>
                  </div>
                  <div className="fee-line">
                    <span>Booking Fee</span>
                    <span>$1.50</span>
                  </div>
                  <div className="total-line">
                    <span>Total</span>
                    <span>${(calculateTotal() + 1.5).toFixed(2)}</span>
                  </div>
                </div>

                <button
                  className="checkout-button"
                  onClick={handleProceedToCheckout}
                  disabled={selectedSeats.length === 0}
                >
                  Proceed to Checkout
                </button>
              </>
            ) : (
              <div className="no-seats-selected">
                <p>Please select your seats</p>
              </div>
            )}
          </div>
        </div>

        <div className={`seating-container ${animateContent ? "animate" : ""}`}>
          <div className="screen-container">
            <div className="screen">Screen</div>
          </div>

          <div className="seats-container">
            {room.layout.map((row, rowIndex) => (
              <div className="seat-row" key={rowIndex}>
                <div className="row-label">{row.row}</div>
                <div className="seats">
                  {row.seats.map((seat) => (
                    <Seat
                      key={seat.seatId}
                      seat={seat}
                      isSelected={selectedSeats.some((s) => s.seatId === seat.seatId)}
                      onClick={() => handleSeatClick(seat)}
                    />
                  ))}
                </div>
                <div className="row-label">{row.row}</div>
              </div>
            ))}
          </div>

          <SeatLegend />
        </div>
      </div>

      {showTicketPopup && (
        <TicketPopup
          movie={movie}
          cinema={cinema}
          screening={screening}
          seats={selectedSeats}
          onClose={handleCloseTicketPopup}
        />
      )}

      {showTimeoutPopup && <Timeout_Popup onClose={handleCloseTimeoutPopup} />}

      <Footer />
    </div>
  )
}

export default SeatBookingPage
