import React, { useState } from 'react';
import NavbarAdmin from '../../components/NavbarAdmin';
import CinemaLocations from '../../components/Location';
import CinemaRoom from '../../components/CinemaRoom';
import TimeSlot from '../../components/TimeSlot';
import '../styles/Schedule.css';

const Schedule = () => {
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedLocations, setSelectedLocations] = useState<string[]>([]);
  const [selectedRoom, setSelectedRoom] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');

  // You'll get these from props or API
  const startDate = '2024-01-01';
  const endDate = '2024-02-01';

  return (
    <div className="schedule-page">
      <NavbarAdmin />
      <div className="schedule-container">
        <h2>Schedule Movie</h2>
        
        <div className="schedule-form">
          {/* Date Selection */}
          <div className="form-group">
            <label>Select Date:</label>
            <input
              type="date"
              min={startDate}
              max={endDate}
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
            />
          </div>

          {/* Location Selection */}
          <CinemaLocations
            selectedLocations={selectedLocations}
            setSelectedLocations={setSelectedLocations}
          />

          {/* Room Selection */}
          <CinemaRoom
            selectedRoom={selectedRoom}
            setSelectedRoom={setSelectedRoom}
          />

          {/* Time Slot Selection */}
          <TimeSlot
            selectedTimeSlot={selectedTimeSlot}
            setSelectedTimeSlot={setSelectedTimeSlot}
          />

          <div className="buttons">
            <button className="schedule-button">Schedule Movie</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Schedule;
