import React from "react";
import "../users/styles/ticket_popup.css";

interface TicketPopUpProps {
  movieTitle: string;
  cinemaName: string;
  roomName: string;
  date: string;
  time: string;
  seats: string[];
  pricePerTicket: number;
  onConfirm: () => void;
  onCancel: () => void;
  remainingTime: string;
}

const TicketPopup: React.FC<TicketPopUpProps> = ({
  movieTitle,
  cinemaName,
  roomName,
  date,
  time,
  seats,
  pricePerTicket,
  onConfirm,
  onCancel,
  remainingTime,
}) => {
  const total = seats.length * pricePerTicket;

  return (
    <div className="ticket-popup-wrapper">
      <div className="ticket-popup">
        <h2 className="popup-title">{movieTitle}</h2>
        <p className="popup-meta">
          {cinemaName} | {roomName}
          <br />
          {date} | {time} | {seats.join(", ")}
        </p>

        <div className="popup-pricing">
          <h4>SINGLE</h4>
          <p>RM {pricePerTicket.toFixed(2)}</p>
          <p>1 movie ticket</p>
          <p>1 F&B combo with drink.</p>
          <p className="ticket-count">{seats.length}</p>
          <p className="ticket-total">RM {total.toFixed(2)}</p>
        </div>

        <div className="popup-timer">
          ⏰ TIME LEFT: <strong>{remainingTime}</strong>
        </div>

        <button className="confirm-cart" onClick={onConfirm}>
          ADD TO CART – RM {total.toFixed(2)}
        </button>

        <button className="cancel-button" onClick={onCancel}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default TicketPopup;
