"use client";

import { useState } from "react";
import Image from "next/image";
import Link from "next/link";
import { ChevronRight, MapPin, User } from "lucide-react";
import { cn } from "@/lib/utils";

export default function CinemaWebsite() {
  const [activeTab, setActiveTab] = useState("NOW SHOWING");

  const tabs = [
    "NOW SHOWING",
    "KIDS SPECIAL",
    "BOOK EARLY",
    "COMING SOON",
    "GEMBIRAYA",
  ];

  const movies = [
    {
      id: 1,
      title: "NE ZHA 2 哪吒之魔童闯海",
      image: "/placeholder.svg?height=500&width=350",
      tag: "2X MOVIEMONEY",
      tagColor: "bg-red-600",
      rating: "P12",
      hasBookNow: true,
      date: "13.03.25",
    },
    {
      id: 2,
      title: "SNOW WHITE",
      image: "/placeholder.svg?height=500&width=350",
      tag: "BOOK EARLY",
      tagColor: "bg-white text-red-600",
      rating: "P12",
    },
    {
      id: 3,
      title: "PETAKA GUNUNG GEDE",
      image: "/placeholder.svg?height=500&width=350",
      tag: "BOOK EARLY",
      tagColor: "bg-white text-red-600",
      rating: "15",
    },
    {
      id: 4,
      title: "A MINECRAFT MOVIE",
      image: "/placeholder.svg?height=500&width=350",
      releaseDate: "3 APRIL 2025",
      rating: "P12",
    },
    {
      id: 5,
      title: "SEVENTEEN [RIGHT HERE] WORLD TOUR IN CINEMAS",
      image: "/placeholder.svg?height=500&width=350",
      tag: "SALUTEEE!",
      tagColor: "bg-white text-red-600",
      rating: "P12",
    },
    {
      id: 6,
      title: "SIKAN...",
      image: "/placeholder.svg?height=500&width=350",
      rating: "15",
    },
  ];

  return (
    <div className="min-h-screen bg-black text-white">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="logo">YourCinema</div>
        <ul className="nav-links">
          <li>
            <a href="/now-showing">Now Showing</a>
          </li>
          <li>
            <a href="/cinemas">Cinemas</a>
          </li>
          <li>
            <a href="/promotions">Promotions</a>
          </li>
          <li>
            <a href="/contact">Contact Us</a>
          </li>
        </ul>
      </nav>

      {/* Hero Section */}
      <section
        style={{
          background: "url('/assets/banner.png') no-repeat center/cover",
          height: "400px",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          color: "white",
          textAlign: "center",
        }}
      >
        <div>
          <h1>Welcome to YourCinema</h1>
          <p>Experience the magic of movies like never before!</p>
          <button
            onClick={() => (window.location.href = "/movies")}
            style={{
              padding: "10px 20px",
              fontSize: "1rem",
              backgroundColor: "#ff6f61",
              border: "none",
              borderRadius: "5px",
              color: "white",
              cursor: "pointer",
            }}
          >
            Book Tickets Now
          </button>
        </div>
      </section>

      {/* Movie Tabs */}
      <div className="container mx-auto px-4 mt-12">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-8 overflow-x-auto pb-2">
            {tabs.map((tab) => (
              <button
                key={tab}
                className={cn(
                  "font-medium text-lg whitespace-nowrap pb-2 relative",
                  activeTab === tab ? "text-white" : "text-gray-400"
                )}
                onClick={() => setActiveTab(tab)}
              >
                {tab}
                {activeTab === tab && (
                  <div className="absolute bottom-0 left-0 w-full h-1 bg-red-600"></div>
                )}
              </button>
            ))}
          </div>
          <Link href="/all-movies" className="flex items-center gap-1 text-sm">
            VIEW ALL
            <ChevronRight size={16} />
          </Link>
        </div>

        {/* Movie Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-5 xl:grid-cols-6 gap-4 mt-8 relative">
          {movies.map((movie) => (
            <div
              key={movie.id}
              className="relative border border-red-900 rounded-lg overflow-hidden"
            >
              {/* Movie Poster */}
              <div className="relative aspect-[2/3]">
                <Image
                  src={movie.image || "/placeholder.svg"}
                  alt={movie.title}
                  fill
                  className="object-cover"
                />

                {/* Tags */}
                {movie.tag && (
                  <div
                    className={`absolute top-2 left-2 ${movie.tagColor} text-xs font-bold px-2 py-1 rounded`}
                  >
                    {movie.tag}
                  </div>
                )}

                {/* Release Date */}
                {movie.releaseDate && (
                  <div className="absolute bottom-16 left-0 right-0 text-center font-bold text-lg">
                    {movie.releaseDate}
                  </div>
                )}

                {/* Date */}
                {movie.date && (
                  <div className="absolute bottom-24 left-0 right-0 text-center font-bold text-lg">
                    {movie.date}
                  </div>
                )}

                {/* Rating */}
                <div
                  className={`absolute bottom-4 left-4 ${
                    movie.rating === "15" ? "bg-orange-500" : "bg-green-500"
                  } text-xs font-bold px-2 py-1 rounded-md`}
                >
                  {movie.rating}
                </div>

                {/* Book Now Button */}
                {movie.hasBookNow && (
                  <div className="absolute bottom-12 left-0 right-0 flex justify-center">
                    <button className="bg-white text-red-600 font-bold px-6 py-2 rounded-md">
                      BOOK NOW
                    </button>
                  </div>
                )}
              </div>

              {/* Movie Title */}
              <div className="p-4">
                <h3 className="font-bold text-sm">{movie.title}</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
